import React, { useState, useContext } from 'react';
import AuthForm from '../components/AuthForm';
import api from '../utils/api';
// navigate потрібен для перенаправлення в початковій перевірці
import { useNavigate } from 'react-router-dom';
import { auth, googleProvider } from '../firebase';
import { signInWithPopup } from 'firebase/auth';
import { UserContext } from '../contexts/UserContext';

export default function LoginPage() {
    const navigate = useNavigate();

    // Отримуємо user та handleLoginSuccess з UserContext
    // user потрібен для початкової перевірки, handleLoginSuccess для повідомлення контексту про успіх
    const { user, handleLoginSuccess } = useContext(UserContext);

    // Стан завантаження для кнопки/форми
    const [loginInProgress, setLoginInProgress] = useState(false);
    // Стан для відображення помилок форми
    const [formError, setFormError] = useState(null);

    // --- Перенаправлення, якщо користувач вже залогінений (перевірка стану з контексту) ---
    // Цей блок ПЕРЕД рендерингом форми. Якщо user з контексту існує, ми одразу перенаправляємо.
    // Це запобігає короткочасному показу форми логіну автентифікованим користувачам
    // до того, як спрацює основний ефект перенаправлення в App.js.
    // Використовуємо navigate hook.
    if (user) {
        console.log('LoginPage: User is already logged in, navigating to /.');
        navigate('/', { replace: true }); // Використовуємо navigate для перенаправлення
        return null; // Рендеримо null під час навігації
    }


    // --- Логіка стандартного логіну (email/password) ---
    // *** ВИПРАВЛЕНО: Сигнатура функції приймає ОБ'ЄКТ { email, password } ***
    // Деструктуризуємо об'єкт, щоб отримати email та password
    const handleLogin = async ({ email, password }) => {
        setLoginInProgress(true); // Встановлюємо стан завантаження
        setFormError(null); // Очищаємо попередні помилки форми

        console.log('LoginPage: Attempting password login with data:', { email, password }); // Лог даних перед відправкою на бекенд

        try {
            // *** ВИКОРИСТОВУЄМО email та password, ВИЯТНУТІ З ОБ'ЄКТА ***
            // Відправляємо на бекенд об'єкт { email: '...', password: '...' }
            const res = await api.post('/auth/login/password', { email, password }, {
                withCredentials: true
            });
            console.log('LoginPage: Password login success. Response received.');

            // --- ПРАВИЛЬНИЙ ДОСТУП ДО ДАНИХ З ВІДПОВІДІ ВИКОРИСТОВУЮЧИ ДЕСТРУКТУРИЗАЦІЮ ---
            const { user } = res.data; // Отримуємо вкладений об'єкт user
            // --- КІНЕЦЬ ДЕСТРУКТУРИЗАЦІЇ ---

            // --- ЗБЕРІГАННЯ В localStorage ---
            // Використовуємо дані з деструктурованого об'єкта user
            localStorage.setItem('userName', user.name);
            localStorage.setItem('isAdmin', user.is_admin ? 'true' : 'false'); // Зберігаємо булеве як рядок
            localStorage.setItem('userId', user.id); // *** ЗБЕРІГАЄМО ID КОРИСТУВАЧА ***
            console.log('LoginPage: userName, isAdmin, userId saved to localStorage.');
            // --- КІНЕЦЬ ЗБЕРІГАННЯ ---


            // --- ПОВІДОМЛЯЄМО КОНТЕКСТ ПРО УСПІХ ---
            // Передаємо ВЕСЬ деструктурований об'єкт user в контекст
            // Контекст оновить свої стани (user, isAdmin) та запустить логіку перевірки пристрою (checkDeviceStatus).
            handleLoginSuccess(user); // Передаємо деструктурований об'єкт user

            // *** Не перенаправляємо тут! Навігацію контролює App.js на основі стану контексту (user, hasDevice). ***

        } catch (err) {
            // Обробка помилки логіну
            console.error('LoginPage: Password login error:', err.response?.data || err.message);
            const errorMessage = err.response?.data?.message || 'Помилка входу';
            setFormError(errorMessage); // Встановлюємо помилку для відображення у формі
            alert('Помилка входу: ' + errorMessage); // Також показуємо спливаюче повідомлення
        } finally {
            // Завжди завершуємо стан завантаження, незалежно від успіху чи помилки API логіну
            setLoginInProgress(false);
        }
    };

    // --- Логіка логіну через Google ---
    // Ця функція не приймає аргументів email/password/name від форми.
    const handleGoogleLogin = async () => {
        setLoginInProgress(true); // Встановлюємо стан завантаження
        setFormError(null); // Очищаємо попередні помилки форми

        console.log('LoginPage: Initiating Google sign-in popup.');
        try {
            const result = await signInWithPopup(auth, googleProvider);
            // Отримуємо ID токен від Firebase Auth
            const token = await result.user.getIdToken();
            console.log('LoginPage: Firebase Google auth success. Token obtained.');

            console.log('LoginPage: Sending Google token to backend /auth/login/google...');
            const res = await api.post('/auth/login/google', { token }, { withCredentials: true });
            console.log('LoginPage: Backend Google login success. Response received.', res.data);

            // --- ПРАВИЛЬНИЙ ДОСТУП ДО ДАНИХ З ВІДПОВІДІ ВИКОРИСТОВУЮЧИ ДЕСТРУКТУРИЗАЦІЮ ---
            const { user } = res.data; // Отримуємо вкладений об'єкт user
            // --- КІНЕЦЬ ДЕСТРУКТУРИЗАЦІЇ ---

            // --- ЗБЕРІГАННЯ В localStorage ---
            localStorage.setItem('userName', user.name);
            localStorage.setItem('isAdmin', user.is_admin ? 'true' : 'false');
            localStorage.setItem('userId', user.id); // *** ЗБЕРІГАЄМО ID КОРИСТУВАЧА ***
            console.log('LoginPage: userName, isAdmin, userId saved to localStorage.');
            // --- КІНЕЦЬ ЗБЕРІГАННЯ ---


            // --- ПОВІДОМЛЯЄМО КОНТЕКСТ ПРО УСПІХ ---
            // Передаємо ВЕСЬ деструктурований об'єкт user в контекст
            handleLoginSuccess(user);


            // *** Не перенаправляємо тут! Навігацію контролює App.js на основі стану контексту. ***


        } catch (error) {
            // Обробка помилки авторизації через Google
            console.error('LoginPage: Google login error:', error);
            const errorMessage = error.message || 'Помилка авторизації через Google';
            setFormError(errorMessage); // Встановлюємо помилку для відображення у формі
            alert('Помилка авторизації через Google: ' + errorMessage); // Також показуємо спливаюче повідомлення
        } finally {
            setLoginInProgress(false);
        }
    };


    return (
        <>
            {formError && <div style={{ color: 'red', marginBottom: '10px', textAlign: 'center' }}>{formError}</div>}

            <AuthForm
                type="login" // Тип форми: 'login' або 'register'
                onSubmit={handleLogin} // Передаємо виправлений обробник
                onGoogleLogin={handleGoogleLogin}
                isSubmitting={loginInProgress} // Пропс для відображення стану завантаження у формі
            />
        </>
    );
}
