import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import api from '../utils/api';

const Field = () => {
    const { id } = useParams();
    const [field, setField] = useState(null);
    const [points, setPoints] = useState([]);

    // 1. Спочатку встановлюємо це поле як selected
    const selectField = async () => {
        try {
            await api.post('/fields/select/', { fieldId: id });
        } catch (err) {
            console.error('Не вдалося вибрати поле:', err);
        }
    };

    // 2. Отримуємо точки для обраного поля
    const fetchPoints = async () => {
        setPoints([]); // Очищаємо попередні точки, щоб не моргало неправильно
        try {
            const response = await api.get('/measurement-points/points');
            setPoints(response.data.points);
        } catch (error) {
            console.error("Error fetching points:", error);
        }
    };



    // 3. Активація/деактивація точки
    const activatePoint = (pointId) => {
        api.post('/measurement-points/activate', { pointId })
            .then(() => fetchPoints())
            .catch(err => console.error('Помилка при активації точки:', err));
    };

    const deactivatePoint = (pointId) => {
        api.post('/measurement-points/deactivate', { pointId })
            .then(() => fetchPoints())
            .catch(err => console.error('Помилка при деактивації точки:', err));
    };

    useEffect(() => {
        const init = async () => {
            try {
                setPoints([]); // Очистити точки одразу при зміні поля

                const fieldResponse = await api.get(`/fields/${id}`);
                setField(fieldResponse.data);

                await api.post('/fields/select/', { fieldId: id });

                await fetchPoints(); // дочекатися перед оновленням UI

                const interval = setInterval(fetchPoints, 60000);
                return () => clearInterval(interval);
            } catch (err) {
                console.error("Помилка ініціалізації поля:", err);
            }
        };

        init();
    }, [id]);



    if (!field) return <div className="p-4">Завантаження...</div>;

    return (
        <div className="p-6 w-full flex-1">
            <h1 className="text-3xl font-bold mb-2">{field.name}</h1>
            <p className="text-gray-600 mb-4">{field.description}</p>

            <div className="grid grid-cols-2 gap-4">
                <p><strong>Площа:</strong> {field.area} га</p>
                <p><strong>Тип ґрунту:</strong> {field.soilType}</p>
                <p><strong>Створено:</strong> {field.created_at}</p>
                <p><strong>Оновлено:</strong> {new Date(field.updatedAt).toLocaleDateString()}</p>
            </div>

            <hr className="my-6" />

            <h2 className="text-xl font-semibold mb-2">Точки поля</h2>
            <ul className="list-disc ml-6 space-y-2">
                {points.length === 0 ? (
                    <p className="text-gray-500 italic">Завантаження точок...</p>
                ) : (
                    points.map((point) => (
                        <li key={point.id} className="flex justify-between items-center">
        <span>
          #{point.point_order} — Широта: {point.latitude}, Довгота: {point.longitude} —
          Статус: {point.active ? 'Активна' : 'Неактивна'}
        </span>
                            <div>
                                {point.active ? (
                                    <button
                                        onClick={() => deactivatePoint(point.id)}
                                        className="text-red-500 hover:underline"
                                    >
                                        Деактивувати
                                    </button>
                                ) : (
                                    <button
                                        onClick={() => activatePoint(point.id)}
                                        className="text-green-500 hover:underline"
                                    >
                                        Активувати
                                    </button>
                                )}
                            </div>
                        </li>
                    ))
                )}
            </ul>

        </div>
    );
};

export default Field;
