import React, { useEffect, useState, useMemo } from 'react';
import api from '../../utils/api';
import {useTranslation} from "react-i18next";

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedUser, setSelectedUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { t } = useTranslation();

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            setLoading(true);
            setError('');
            const res = await api.get('/users');
            setUsers(res.data);
        } catch (err) {
            console.error('Failed to fetch users:', err);
            setError(t('failed_load_users'));
        } finally {
            setLoading(false);
        }
    };

    const handleUserClick = async (user) => {
        if (selectedUser?.id === user.id) {
            console.log(`User ${user.id} clicked again, deselecting.`);
            setSelectedUser(null);
            return;
        }

        console.log(`Workspaceing details for user ${user.id}...`);
        try {
            setError('');
            const res = await api.get(`/users/${user.id}`);
            setSelectedUser(res.data);
            console.log(`Details fetched for user ${user.id}.`);
        } catch (err) {
            console.error(`Failed to fetch details for user ${user.id}:`, err);
            setError(t('failed_load_user_details'));
            setSelectedUser(null);
        }
    };


    const handleDelete = async (id) => {
        if (!window.confirm(t('confirm_delete_user'))) return;

        try {
            setError('');
            await api.delete(`/users/${id}`);
            setUsers(users.filter(user => user.id !== id));
            if (selectedUser?.id === id) {
                setSelectedUser(null);
            }
            console.log(`User ${id} deleted successfully.`);
        } catch (err) {
            console.error(`Failed to delete user ${id}:`, err);
            setError(t('failed_delete_user'));
        }
    };

    const filteredUsers = useMemo(() => {
        if (!searchTerm) {
            return users;
        }
        const lowerCaseSearchTerm = searchTerm.toLowerCase();
        return users.filter(user =>
            user.name.toLowerCase().includes(lowerCaseSearchTerm) ||
            String(user.id).toLowerCase().includes(lowerCaseSearchTerm)
        );
    }, [users, searchTerm]);

    // Визначаємо класи для Grid колонок. auto для номера, 1fr для імені/імейла, auto для дій
    // Використовуємо minmax(0, auto) для гнучкості та запобігання переповнення
    const gridColsClasses = "grid grid-cols-[minmax(0,auto)_1fr_minmax(0,auto)] gap-4";


    return (
        <div className="p-4 overflow-y-auto max-h-screen w-full">
            <h2 className="text-xl font-bold mb-4">{t(`users`)}</h2>

            {/* Поле для пошуку */}
            <div className="mb-4">
                <input
                    type="text"
                    placeholder={t('search_users')}
                    value={searchTerm}
                    onChange={(e) => {
                        setSearchTerm(e.target.value);
                        setSelectedUser(null); // Очищаємо вибраного користувача при зміні пошуку
                    }}
                    className="border p-2 rounded w-full"
                />
            </div>

            {loading && <p>{t(`loading`)}</p>}
            {error && <p className="text-red-500">{error}</p>}

            {/* Повідомлення про відсутність користувачів */}
            {!loading && !error && filteredUsers.length === 0 && (
                <p className="text-gray-500">
                    {searchTerm
                        ? t('no_users_matching_search')
                        : t('no_users_found')
                    }
                </p>
            )}

            {/* Мапуємо ВІДФІЛЬТРОВАНИЙ список користувачів */}
            {filteredUsers.map((user, index) => ( // <-- Отримуємо index
                <React.Fragment key={user.id}>
                    {/* Div елемента списку користувача - GRID КОНТЕЙНЕР */}
                    <div
                        onClick={() => handleUserClick(user)}
                        // Комбінуємо існуючі класи з класами Grid
                        className={`border p-3 rounded mb-2 items-center cursor-pointer transition-colors duration-150
                            ${gridColsClasses} {/* grid grid-cols-[minmax(0,auto)_1fr_minmax(0,auto)] gap-4 */}
                            ${selectedUser?.id === user.id
                            ? 'bg-blue-100 border-blue-500'
                            : 'border-gray-300 hover:bg-gray-100'
                        }`}
                    >
                        {/* <-- ПЕРША "КОЛОНКА": НОМЕР ЕЛЕМЕНТА + ВЕРТИКАЛЬНИЙ РОЗДІЛЮВАЧ --> */}
                        {/* Використовуємо div для номера з наданими стилями */}
                        <div className="h-6 px-3 flex-shrink-0 text-center text-l font-semibold text-gray-700 bg-gray-100 shadow-md rounded-md whitespace-nowrap flex items-center justify-center"> {/* Додано flex/items-center/justify-center для центрування вмісту всередині */}
                            №{index + 1} {/* <-- Відображаємо індекс + 1 як номер елемента */}
                        </div>

                        {/* <-- ДРУГА "КОЛОНКА": Ім'я та Email --> */}
                        {/* Цей div буде йти після першої колонки Grid, а вертикальний розділювач візуально стане між ними за рахунок свого mx */}
                        {/* Обгортаємо існуючі абзаци в div */}
                        <div>
                            <p><strong>{user.name}</strong></p>
                            <p className="text-sm text-gray-500">{user.email}</p>
                        </div>

                        {/* <-- ТРЕТЯ "КОЛОНКА": Кнопка Видалити --> */}
                        <div className="flex gap-2 justify-end">
                            <button
                                onClick={(e) => {
                                    e.stopPropagation();
                                    handleDelete(user.id);
                                }}
                                className="text-red-500 hover:underline"
                            >
                                {t(`delete`)}
                            </button>
                        </div>
                    </div>

                    {/* Вертикальний розділювач - додаємо його ПІСЛЯ головного div користувача, якщо немає деталей */}
                    {/* Він буде візуально розділяти елементи списку */}
                    {!selectedUser?.id === user.id && ( // Рендеримо розділювач, якщо деталі не відкриті під цим елементом
                        <div className="w-full h-px bg-gray-300 mb-2"></div> // Горизонтальна лінія між елементами
                    )}
                    {/* Видалено попередній вертикальний розділювач div className="w-px h-16 bg-gray-300 mx-4" */}
                    {/* Попередній розділювач був, ймовірно, для іншого макету */}


                    {/* Умовно рендеримо деталі КОНКРЕТНОГО користувача, якщо він вибраний */}
                    {selectedUser?.id === user.id && (
                        <div className="bg-white p-4 rounded shadow mb-4 ml-4 mr-4">
                            <h3 className="text-lg font-semibold mb-2">{t(`user_information`)}</h3>
                            <div className="space-y-2">
                                <p><strong>ID:</strong> {selectedUser.id}</p>
                                <p><strong>{t(`first_name`)}:</strong> {selectedUser.name}</p>
                                <p><strong>Email:</strong> {selectedUser.email}</p>
                                <p><strong>{t(`admin`)}:</strong> {selectedUser.is_admin ? t(`yes`) : t(`no`)}</p>
                                {selectedUser.profile_picture_url && (
                                    <p><strong>{t(`profile_picture`)}:</strong> <img src={selectedUser.profile_picture_url} alt="Profile" className="w-10 h-10 rounded-full inline-block ml-2" /></p>
                                )}
                            </div>
                        </div>
                    )}
                </React.Fragment>
            ))}
            {/* Додаємо горизонтальну лінію після останнього елемента списку, якщо немає помилок і список не порожній */}
            {!loading && !error && filteredUsers.length > 0 && (
                <div className="w-full h-px bg-gray-300 mb-2"></div>
            )}
        </div>
    );
};

export default AdminUsers;
