import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

// Створення контексту користувача
export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    const [user, setUser] = useState(null); // Дані користувача
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Статус авторизації
    const [loading, setLoading] = useState(true); // Статус завантаження

    // Функція для перевірки статусу авторизації
    const checkAuthStatus = async () => {
        try {
            const response = await axios.get('/auth/status', { withCredentials: true });
            setUser(response.data.user);
            setIsAuthenticated(true);
        } catch (error) {
            setIsAuthenticated(false);
            setUser(null);
        } finally {
            setLoading(false);
        }
    };

    // Перевірка статусу авторизації при завантаженні компоненту
    useEffect(() => {
        checkAuthStatus();
    }, []);

    return (
        <UserContext.Provider value={{ user, isAuthenticated, loading, checkAuthStatus }}>
            {children}
        </UserContext.Provider>
    );
};
