import React, { useEffect, useState } from 'react';
import api from '../../utils/api';
import {useTranslation} from "react-i18next";

const AdminUserDevices = () => {
    const [users, setUsers] = useState([]); // Needed for User dropdown
    const [devices, setDevices] = useState([]); // Needed for Device dropdown
    const [associations, setAssociations] = useState([]); // List of associations with included User and IotDevice
    const [newAssociation, setNewAssociation] = useState({ user_id: '', iot_device_id: '' });
    const { t } = useTranslation();

    // Fetch users for the dropdown
    const fetchUsers = async () => {
        try {
            const res = await api.get('/users'); // Assumes /users returns [{ id, email, ... }]
            setUsers(res.data);
        } catch (err) {
            console.error('Error fetching users:', err);
        }
    };

    // Fetch devices for the dropdown
    const fetchDevices = async () => {
        try {
            // Assumes /iot-devices returns [{ id, device_number, ... }] or similar
            const res = await api.get('/iot-devices', { withCredentials: true });
            setDevices(res.data);
        } catch (err) {
            console.error('Error fetching devices:', err);
        }
    };

    // Fetch associations - this endpoint should return association objects
    // WITH nested User and IotDevice objects
    const fetchAssociations = async () => {
        try {
            // This calls the controller method getAllUserDevices
            const res = await api.get('/user-iot-devices/all');
            // Expecting res.data to be like [{ id: assocId, user_id: ..., iot_device_id: ..., User: {...}, IotDevice: {...} }, ...]
            setAssociations(res.data);
            console.log('Fetched associations:', res.data); // Log fetched data to verify structure
        } catch (err) {
            console.error('Error fetching associations:', err);
        }
    };

    useEffect(() => {
        // Fetch all data needed for the component
        fetchUsers();
        fetchDevices();
        fetchAssociations(); // This fetches the list to display
    }, []); // Empty dependency array means fetch data only on mount

    // Handler for binding a device to a user
    const handleBind = async () => {
        // Check if both user and device are selected
        if (!newAssociation.user_id || !newAssociation.iot_device_id) {
            alert("Будь ласка, оберіть користувача та пристрій.");
            return;
        }
        try {
            // Assumes POST /user-iot-devices expects { user_id, iot_device_id }
            await api.post('/user-iot-devices', newAssociation);
            setNewAssociation({ user_id: '', iot_device_id: '' }); // Reset form
            fetchAssociations(); // Refresh the list after binding
        } catch (error) {
            console.error('Помилка при прив’язці пристрою:', error);
            alert('Помилка при прив’язці пристрою: ' + (error.response?.data?.message || error.message));
        }
    };

    // Handler for unbinding a device from a user
    const handleUnbind = async (association) => {
        // Assumes DELETE /user-iot-devices expects { user_id, iot_device_id } in body
        // Note: DELETE with body might require backend configuration
        // A more typical RESTful approach is DELETE /user-iot-devices/:id where id is the association ID
        // Or DELETE /user-iot-devices?user_id=...&iot_device_id=...
        // If your backend expects body for DELETE:
        try {
            await api.delete('/user-iot-devices', {
                data: { // Axios uses 'data' for DELETE body
                    user_id: association.user_id,
                    iot_device_id: association.iot_device_id
                }
            });
            fetchAssociations(); // Refresh the list after unbinding
        } catch (error) {
            console.error('Помилка при відв’язці пристрою:', error);
            alert('Помилка при відв’язці пристрою: ' + (error.response?.data?.message || error.message));
        }
        /*
        // Alternative DELETE by association ID (if your backend supports DELETE /user-iot-devices/:id)
        try {
            await api.delete(`/user-iot-devices/${association.id}`); // Assumes assoc has id
            fetchAssociations(); // Refresh the list
        } catch (error) {
            console.error('Помилка при відв’язці пристрою:', error);
            alert('Помилка при відв’язці пристрою: ' + (error.response?.data?.message || error.message));
        }
        */
    };

    return (
        <div className="p-4">
            <h2 className="text-xl font-semibold mb-4">{t(`users_devices_management`)}</h2>

            {/* Binding Form */}
            <div className="mb-4 grid grid-cols-3 gap-2">
                <select
                    value={newAssociation.user_id}
                    onChange={(e) => setNewAssociation({ ...newAssociation, user_id: e.target.value })}
                    className="border p-2"
                >
                    <option value="">{t(`select_user`)}</option>
                    {/* Map over fetched users */}
                    {users.map(user => (
                        <option key={user.id} value={user.id}>{user.email}</option>
                    ))}
                </select>

                <select
                    value={newAssociation.iot_device_id}
                    onChange={(e) => setNewAssociation({ ...newAssociation, iot_device_id: e.target.value })}
                    className="border p-2"
                >
                    <option value="">{t(`select_device`)}</option>
                    {/* Map over fetched devices */}
                    {devices.map(device => (
                        // Use device.device_number based on model/backend
                        <option key={device.id} value={device.id}>{device.device_name || device.id}</option>
                    ))}
                </select>

                <button onClick={handleBind} className="bg-blue-500 text-white p-2 rounded">
                    {t(`bind`)}
                </button>
            </div>

            {/* Associations Table */}
            <h3 className="text-lg font-semibold mb-2">{t(`existing_bindings`)}</h3>
            {associations.length === 0 && (
                <p>{t(`existing_bindings_exceptions`)}</p>
            )}
            {associations.length > 0 && (
                <table className="w-full border border-collapse">
                    <thead>
                    <tr> {/* Фон залишається на рядку */}
                        {/* Змінено text-left на text-center для кожного заголовка */}
                        <th className="border p-2 text-center">ID</th> {/* <-- Змінено */}
                        <th className="border p-2 text-center">{t(`user`)}</th> {/* <-- Змінено */}
                        <th className="border p-2 text-center">{t(`device`)}</th> {/* <-- Змінено */}
                        {/* Виправлений одрук, додано ширину, змінено вирівнювання тексту */}
                        <th className="border p-2 text-center w-1/6">{t(`actions`)}</th> {/* <-- Змінено */}
                    </tr>
                    </thead>
                    <tbody>
                    {/* ... решта тіла таблиці ... */}
                    {associations.map((assoc) => (
                        <tr key={assoc.id || `${assoc.user_id}-${assoc.iot_device_id}`}>
                            <td className="border p-2">{assoc.id}</td>
                            <td className="border p-2">{assoc.User?.email || '—'}</td>
                            <td className="border p-2">{assoc.IotDevice?.device_name || '—'}</td>
                            <td className="border p-2 w-1/6">
                                <div className="flex justify-center space-x-2">
                                    <button
                                        onClick={() => handleUnbind(assoc)}
                                        className="bg-red-500 text-white p-1 px-2 rounded"
                                    >
                                        {t(`unbind`)}
                                    </button>
                                </div>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};
export default AdminUserDevices;
