import React, { useState, useEffect } from 'react';
import axios from 'axios';
import api from '../utils/api';
import {useTranslation} from "react-i18next";
import {useUserContext} from "../contexts/UserContext";
const apiBaseUrl = process.env.REACT_APP_API_BASE_URL;
export default function UserMenu() {
    // Отримуємо користувача з контексту для початкової перевірки та можливого початкового стану
    const { user: userFromContext, handleLoginSuccess } = useUserContext();
    const { t } = useTranslation();

    const [user, setUser] = useState(null);

    // Локальні стани для полів форми
    const [name, setName] = useState("");
    const [email, setEmail] = useState(""); // Ймовірно, email не редагується

    const [hasPassword, setHasPassword] = useState(false); // Чи є пароль для зміни

    const [profilePicture, setProfilePicture] = useState(null);

    // Локальні стани для полів зміни пароля
    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPasswordState] = useState("");
    const [confirmPassword, setConfirmPasswordState] = useState("");

    const [message, setMessage] = useState(""); // Повідомлення про статус (успіх/помилка)



    useEffect(() => {
        const storedName = localStorage.getItem('userName');
        const storedAvatar = localStorage.getItem('profilePicture'); // URL або base64

        // *** ДОДАЙТЕ ЦІ ЛОГИ ***
        console.log('UserMenu useEffect: Reading from localStorage');
        console.log('UserMenu useEffect: storedName:', storedName);
        console.log('UserMenu useEffect: storedAvatar (profilePicture):', storedAvatar);
        // *** КІНЕЦЬ ЛОГІВ ***


        if (storedName) {
            setUserName(storedName);
        }

        if (storedAvatar) {
            setProfilePicture(storedAvatar);
        }

        // *** ДОДАЙТЕ ЦЕЙ ЛОГ ПІСЛЯ ОНОВЛЕННЯ СТАНУ ***
        console.log('UserMenu useEffect: profilePicture state after setting:', storedAvatar); // Виводимо те, що встановили
        // Або можна додати окремий useEffect для відстеження стану profilePicture
        // useEffect(() => { console.log('UserMenu state profilePicture changed:', profilePicture); }, [profilePicture]);
        // *** КІНЕЦЬ ЛОГУ ***


    }, []);

    const toggleMenu = () => {
        setIsMenuVisible(!isMenuVisible);
    };

    const handleLogoutClick = () => {
        setShowLogoutConfirm(true);
    };

    const confirmLogout = async () => {
        try {
            await api.post('/auth/logout');
            localStorage.removeItem('userName');
            localStorage.removeItem('isAdmin');
            window.location.href = '/login'; // або твоя сторінка логіну
        } catch (error) {
            console.error('Logout failed:', error);
        } finally {
            setShowLogoutConfirm(false);
        }
    };

    const cancelLogout = () => {
        setShowLogoutConfirm(false);
    };

    return (
        <div className="flex relative inline-block pr-6">
            <div className="relative text-sm">
                <button className="flex items-center focus:outline-none mr-3" onClick={toggleMenu}>
                    <span className="hidden md:inline-block">Hi, {userName }</span>
                    {profilePicture ? (
                        // Якщо обрано новий файл - показуємо його попередній перегляд
                        <div className="mt-2">
                            <p className="text-sm text-gray-600">{t('preview_photo')}</p>
                            <img
                                src={URL.createObjectURL(profilePicture)}
                                alt={t('preview_photo')}
                                className="w-24 h-24 rounded-full object-cover border border-blue-500"
                            />
                            {/* TODO: Додати useEffect Cleanup для відкликання ObjectURL */}
                        </div>
                    ) : (
                        // Якщо новий файл НЕ обрано, але є URL поточного фото в стані user - показуємо його
                        user.profile_picture_url && (
                            <div className="mt-2">
                                <p className="text-sm text-gray-600">{t('current_photo')}</p>
                                <img

                                    src={`http://localhost:5000${user.profile_picture_url}`}
                                    alt={user?.name ? `${t('current_photo')} ${user.name}` : t('current_photo')}
                                    className="w-24 h-24 rounded-full object-cover border border-gray-300"
                                />
                                {/* TODO: Додати кнопку "Видалити фото профілю" */}
                            </div>
                        )
                    )}
                    <svg className="pl-2 h-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 129 129">
                        <path d="m121.3,34.6c-1.6-1.6-4.2-1.6-5.8,0l-51,51.1-51.1-51.1c-1.6-1.6-4.2-1.6-5.8,0-1.6,1.6-1.6,4.2 0,5.8l53.9,53.9c0.8,0.8 1.8,1.2 2.9,1.2 1,0 2.1-0.4 2.9-1.2l53.9-53.9c1.7-1.6 1.7-4.2 0.1-5.8z"/>
                    </svg>
                </button>

                {isMenuVisible && (
                    <div className="bg-white nunito rounded shadow-md mt-2 absolute mt-12 top-15 right-0 min-w-full z-30">
                        <ul className="list-reset">
                            <li><a href="/profile" className="px-3 py-2 block text-gray-900 hover:bg-indigo-400 hover:text-white">{t('my_acc')}</a></li>
                            <li><hr className="border-t mx-2 border-gray-400" /></li>
                            <li><button
                                onClick={handleLogoutClick}
                                className="w-full text-left px-4 py-2 block text-gray-900 hover:bg-red-400 hover:text-white"
                            >
                                {t('logout')}
                            </button></li>
                        </ul>
                    </div>
                )}

                {/* Модальне вікно підтвердження виходу */}
                {showLogoutConfirm && (
                    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
                        <div className="bg-white rounded-lg p-6 w-96 shadow-lg">
                            <h2 className="text-lg font-semibold mb-4">{t('logout_text')}</h2>
                            <div className="flex justify-end space-x-4">
                                <button
                                    onClick={cancelLogout}
                                    className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
                                >
                                    {t('no')}
                                </button>
                                <button
                                    onClick={confirmLogout}
                                    className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                                >
                                    {t('yes')}
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
