import React, { useEffect, useState } from 'react';
import api from '../utils/api';

const Profile = () => {
    // Використовуємо локальний стан для даних, що редагуються на цій сторінці.
    // Отримуємо початкові дані або з контексту, або завантажуємо.
    // Якщо сторінка профілю може бути першою після логіну, краще завантажувати тут.
    const [user, setUser] = useState(null); // Локальний стан користувача
    const [name, setName] = useState("");
    const [email, setEmail] = useState(""); // Якщо email можна редагувати
    const [hasPassword, setHasPassword] = useState(false);

    // !!! ВИПРАВЛЕННЯ: Змінна стану для ФАЙЛУ, ВИБРАНОГО ДЛЯ ЗАВАНТАЖЕННЯ !!!
    const [selectedProfilePictureFile, setSelectedProfilePictureFile] = useState(null);

    // ВИПРАВЛЕННЯ: Правильні назви змінних стану для паролів
    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPasswordState] = useState(""); // Перейменовано, щоб уникнути конфлікту
    const [confirmPassword, setConfirmPasswordState] = useState(""); // Перейменовано

    const [message, setMessage] = useState("");

    // !!! ОТРИМУЄМО handleLoginSuccess з контексту для оновлення глобального стану !!!
    const { handleLoginSuccess: updateUserContext } = useUserContext(); // Перейменовуємо для ясності

    // Валідація форми
    const validateForm = () => {
        setMessage(""); // Очищаємо попередні повідомлення
        if (newPassword || confirmPassword || currentPassword) { // Перевіряємо, якщо хоч одне поле пароля заповнене
            if (!user?.password && currentPassword) {
                setMessage("Ви не можете вказати поточний пароль, якщо у вас його немає.");
                return false;
            }
            if (user?.password && !currentPassword) {
                setMessage("Поточний пароль обов'язковий для зміни пароля.");
                return false;
            }
            if (newPassword !== confirmPassword) {
                setMessage("Новий пароль не співпадає з підтвердженням.");
                return false;
            }
            if (newPassword && newPassword.length < 6) { // Додати валідацію довжини
                setMessage("Новий пароль має бути не менше 6 символів.");
                return false;
            }
        }
        // Додати валідацію імені, email, тощо, якщо потрібно

        return true;
    };


    useEffect(() => {
        const fetchUser = async () => {
            try {
                // Викликаємо ендпоінт /users/me, який тепер має повертати profile_picture_url
                const res = await api.get('/users/me');
                const userData = res.data; // Отримуємо об'єкт користувача з відповіді

                console.log("Profile page fetched user data:", userData); // Лог отриманих даних

                // Встановлюємо локальний стан користувача
                setUser(userData);
                setName(userData.name);
                setEmail(userData.email); // Якщо email можна редагувати
                // Перевіряємо наявність поля password у відповіді для визначення, чи користувач має пароль
                setHasPassword(!!userData.password); // Використовуйте !! щоб перетворити undefined/null/string на boolean

                // !!! Не встановлюємо стан profilePicture (який для ФАЙЛУ) URL-ом !!!
                // setProfilePicture(userData.picture); // ВИДАЛИТИ ЦЕЙ РЯДОК!

            } catch (error) {
                console.error("Помилка завантаження профілю:", error.response?.data || error.message);
                setMessage(error.response?.data?.message || "Помилка завантаження профілю.");
            }
        };

        fetchUser();
    }, []);

    // Обробник вибору файлу
    const handleFileSelect = (e) => {
        const file = e.target.files ? e.target.files[0] : null;
        setSelectedProfilePictureFile(file); // Встановлюємо ВИБРАНИЙ ФАЙЛ у стан
        setMessage(""); // Очищаємо повідомлення при виборі нового файлу
    }

    // Обробник збереження
    const handleSave = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;
        if (!user || !user.id) {
            setMessage("Помилка: Дані користувача не завантажено.");
            return;
        }

        try {
            const formData = new FormData();
            // Додаємо поля, які могли бути змінені
            formData.append("name", name);
            // formData.append("email", email); // Додайте, якщо email можна змінювати

            // !!! ВИПРАВЛЕННЯ: Додаємо ФАЙЛ до FormData з правильним ключем !!!
            // Ключ ("profilePictureFile") має відповідати імені поля, яке очікує Multer на бекенді.
            if (selectedProfilePictureFile) {
                formData.append("profilePictureFile", selectedProfilePictureFile); // <-- Додаємо файл!
            }

            // Додаємо поля пароля, якщо вони заповнені
            if (currentPassword) formData.append("currentPassword", currentPassword);
            if (newPassword) formData.append("newPassword", newPassword);
            if (confirmPassword) formData.append("confirmPassword", confirmPassword);


            // Виклик API для оновлення профілю
            const res = await api.put(`/users/${user.id}`, formData, {
                headers: { "Content-Type": "multipart/form-data" }, // Обов'язковий заголовок для FormData
            });

            const updatedUserData = res.data; // Отримуємо ОНОВЛЕНИЙ об'єкт користувача з бекенду
            console.log("User updated successfully:", updatedUserData);

            // Оновлюємо локальний стан користувача на сторінці профілю
            setUser(updatedUserData);
            setMessage("Зміни успішно збережено.");

            // !!! ВИПРАВЛЕННЯ: Оновлюємо стан користувача у UserContext !!!
            // Це оновить фото профілю у UserMenu та інших компонентах, що використовують контекст.
            updateUserContext(updatedUserData);

            // Очищаємо поля пароля та обраний файл після успішного оновлення
            setCurrentPassword("");
            setNewPasswordState("");
            setConfirmPasswordState("");
            setSelectedProfilePictureFile(null); // Скидаємо стан обраного файлу


        } catch (err) {
            console.error("Error saving profile:", err.response?.data || err.message);
            setMessage(err.response?.data?.message || "Помилка збереження змін.");
        }
    };

    // Хелпери для обробки змін в полях (можна спростити)
    const handleNameChange = (e) => { setName(e.target.value); setMessage(""); }
    const handleCurrentPasswordChange = (e) => { setCurrentPassword(e.target.value); setMessage(""); }
    const handleNewPasswordChange = (e) => { setNewPasswordState(e.target.value); setMessage(""); }
    const handleConfirmPasswordChange = (e) => { setConfirmPasswordState(e.target.value); setMessage(""); }
    // Якщо email можна змінювати, додайте handleEmailChange


    // Показуємо завантаження або повідомлення, якщо дані користувача ще не завантажені
    if (!user) return <p>Завантаження даних профілю...</p>;


    return (
        <div className="w-full md:w-2/3 p-6">
            <h2 className="text-2xl font-semibold mb-4">Мій акаунт</h2>

            <form onSubmit={handleSave} className="space-y-4">
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Фото профілю</label>
                    {/* Інпут для вибору файлу */}
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileSelect} // Використовуємо обробник для встановлення файлу
                        className="w-full"
                    />

                    {/* !!! ВІДОБРАЖАЄМО ПОТОЧНЕ фото профілю (якщо є URL) !!! */}
                    {/* Використовуємо URL з об'єкта user у локальному стані */}
                    {user.profile_picture_url && (
                        <div className="mt-2"> {/* Додайте відступ зверху */}
                            <p className="text-sm text-gray-600 mb-1">Поточне фото:</p> {/* Підпис */}
                            <img
                                src={user.profile_picture_url} // URL беремо з user.profile_picture_url
                                alt="Поточне фото профілю"
                                className="w-24 h-24 rounded-full object-cover border border-gray-300" // Стилі
                            />
                            {/* TODO: Додати кнопку для видалення фото профілю */}
                        </div>
                    )}

                    {/* Опціонально: Відображаємо попередній перегляд НОВОГО обраного файлу перед завантаженням */}
                    {selectedProfilePictureFile && (
                        <div className="mt-2">
                            <p className="text-sm text-gray-600 mb-1">Попередній перегляд нового фото:</p> {/* Підпис */}
                            <img
                                // Створюємо тимчасовий URL для попереднього перегляду файлу
                                src={URL.createObjectURL(selectedProfilePictureFile)}
                                alt="Попередній перегляд нового фото"
                                className="w-24 h-24 rounded-full object-cover border border-blue-500" // Стилі (можна відрізнити від поточного)
                            />
                            {/* !!! ВАЖЛИВО: Очистити тимчасовий URL об'єкта при зміні або розмонтуванні !!! */}
                            {/* Додайте useEffect cleanup: */}
                            {/* useEffect(() => { return () => { if (selectedProfilePictureFile) URL.revokeObjectURL(URL.createObjectURL(selectedProfilePictureFile)); } }, [selectedProfilePictureFile]); */}
                        </div>
                    )}
                </div>

                {/* ... Поля Email, Ім'я ... */}
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Email</label>
                    <input
                        type="email"
                        value={email} // Використовуємо локальний стан
                        disabled // Якщо email незмінний
                        className="w-full px-4 py-2 border border-gray-300 rounded bg-gray-100 cursor-not-allowed"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Ім’я</label>
                    <input
                        type="text"
                        value={name} // Використовуємо локальний стан
                        onChange={handleNameChange}
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>


                {/* Секція зміни пароля (показуємо, якщо у користувача є пароль) */}
                {/* ВИПРАВЛЕННЯ: Перевіряємо user?.password (з об'єкта користувача) */}
                {user?.password && ( // Показуємо, якщо поле password присутнє у об'єкті user
                    <>
                        <hr className="my-4" />
                        <h3 className="text-lg font-semibold">Зміна пароля</h3>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Поточний пароль</label>
                            <input
                                type="password"
                                value={currentPassword} // Використовуємо локальний стан
                                onChange={handleCurrentPasswordChange}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Новий пароль</label>
                            <input
                                type="password"
                                value={newPassword} // Використовуємо локальний стан
                                onChange={handleNewPasswordChange}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Підтвердження пароля</label>
                            <input
                                type="password"
                                value={confirmPassword} // Використовуємо локальний стан
                                onChange={handleConfirmPasswordChange}
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>
                    </>
                )}
                {/* TODO: Обробка випадку, коли у користувача НЕМАЄ пароля (наприклад, Google login) - дозволити встановити? */}


                {message && <p className="text-sm text-blue-600">{message}</p>}

                <button
                    type="submit"
                    className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
                >
                    Зберегти зміни
                </button>
            </form>
        </div>
    );
};

export default Profile;
