import React, { useState } from 'react';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await api.post('/auth/login/password', { email, password });
            console.log(res.data); // тут можна зберегти токен або просто перейти
            navigate('/dashboard'); // перенаправлення після логіну
        } catch (err) {
            alert('Помилка входу: ' + err.response?.data?.message || err.message);
        }
    };

    return (
        <form onSubmit={handleLogin} className="max-w-md mx-auto mt-10 flex flex-col gap-4">
            <h2 className="text-2xl font-bold text-center">Вхід</h2>
            <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                className="border p-2 rounded"
                required
            />
            <input
                type="password"
                placeholder="Пароль"
                value={password}
                onChange={e => setPassword(e.target.value)}
                className="border p-2 rounded"
                required
            />
            <button type="submit" className="bg-blue-500 text-white py-2 rounded hover:bg-blue-600">
                Увійти
            </button>
        </form>
    );
}
