import React, { useCallback, useState, useRef, useEffect } from "react";
import { GoogleMap, Polygon } from "@react-google-maps/api";

const containerStyle = {
    width: "100%",
    height: "100%",
};

const center = {
    lat: 48.3794,
    lng: 31.1656,
};

const MapPolygonPicker = ({ onPolygonComplete, setManualCoordsFromMap }) => {
    const [paths, setPaths] = useState([]);
    const mapRef = useRef(null);

    const handleClick = useCallback((event) => {
        const newPoint = {
            lat: parseFloat(event.latLng.lat().toFixed(6)),
            lng: parseFloat(event.latLng.lng().toFixed(6)),
        };
        setPaths((prev) => [...prev, newPoint]);
    }, []);

    useEffect(() => {
        if (setManualCoordsFromMap && paths.length > 0) {
            const coordsText = paths
                .map((point) => `[${point.lat}, ${point.lng}]`)
                .join(",\n");
            setManualCoordsFromMap(coordsText);
        }
    }, [paths, setManualCoordsFromMap]);

    const handleComplete = () => {
        if (paths.length < 3) {
            alert("Позначте хоча б три точки, щоб утворити полігон");
            return;
        }
        const simplified = paths.map(p => [p.lat, p.lng]);
        if (onPolygonComplete) {
            onPolygonComplete(simplified);
        }
    };

    const handleReset = () => {
        setPaths([]);
        if (setManualCoordsFromMap) {
            setManualCoordsFromMap("");
        }
    };

    return (
        <div className="w-full h-full relative">
            <GoogleMap
                mapContainerStyle={containerStyle}
                center={center}
                zoom={6}
                onClick={handleClick}
                onLoad={(map) => (mapRef.current = map)}
            >
                {paths.length > 0 && (
                    <Polygon
                        path={paths}
                        options={{
                            fillColor: "#00FF00",
                            fillOpacity: 0.3,
                            strokeColor: "#00AA00",
                            strokeOpacity: 1,
                            strokeWeight: 2,
                            editable: true,
                            draggable: false,
                        }}
                    />
                )}
            </GoogleMap>

            <div className="absolute bottom-2 right-2 flex gap-2">
                <button
                    className="bg-green-500 text-white px-3 py-1 rounded text-sm shadow-md"
                    onClick={handleComplete}
                >
                    Підтвердити
                </button>
                <button
                    className="bg-red-500 text-white px-3 py-1 rounded text-sm shadow-md"
                    onClick={handleReset}
                >
                    Скинути
                </button>
            </div>
        </div>
    );
};

export default MapPolygonPicker;
