import React, { useEffect, useState } from 'react';

const Profile = () => {
    // Початкові дані – умовно підставні, замінити на API або context
    const [name, setName] = useState("John Doe");
    const [email] = useState("john.doe@example.com"); // лише для перегляду
    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const res = await api.get(`/users/${userId}`);
                setName(res.data.name);
                setEmail(res.data.email);
                setHasPassword(!!res.data.password); // bool
            } catch (err) {
                console.error("Помилка отримання користувача", err);
            }
        };

        fetchUser();
    }, [userId]);

    const handleSave = (e) => {
        e.preventDefault();
        if (newPassword !== confirmPassword) {
            setMessage("Новий пароль не співпадає з підтвердженням.");
            return;
        }

        // Тут мав би бути запит до API на збереження
        console.log("Зміни збережено:", { name, currentPassword, newPassword });

        setMessage("Зміни успішно збережено.");
        setCurrentPassword("");
        setNewPassword("");
        setConfirmPassword("");
    };

    return (
        <div className="w-full md:w-2/3 p-6">
            <h2 className="text-2xl font-semibold mb-4">Мій акаунт</h2>
            <form onSubmit={handleSave} className="space-y-4">
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Email</label>
                    <input
                        type="email"
                        value={email}
                        disabled
                        className="w-full px-4 py-2 border border-gray-300 rounded bg-gray-100 cursor-not-allowed"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Ім’я</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                <hr className="my-4" />

                <h3 className="text-lg font-semibold">Зміна пароля</h3>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Поточний пароль</label>
                    <input
                        type="password"
                        value={currentPassword}
                        onChange={(e) => setCurrentPassword(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Новий пароль</label>
                    <input
                        type="password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Підтвердження пароля</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                {message && <p className="text-sm text-blue-600">{message}</p>}

                <button
                    type="submit"
                    className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
                >
                    Зберегти зміни
                </button>
            </form>
        </div>
    );
};

export default Profile;
