import React, { useState } from "react";
import DashboardContent from "../components/DashboardContent";
import MainContent from "../components/MainContent";
import CreateField from "../components/CreateField";  // Імпортуємо CreateField

const HomePage = () => {
    const [isCreatingField, setIsCreatingField] = useState(false);

    const handleCancel = () => {
        setIsCreatingField(false);  // Повертаємося до попереднього контенту
    };

    return (
        <div className="flex">
            <DashboardContent />
            {isCreatingField ? (
                <CreateField onCancel={handleCancel} />  // Якщо створення поля, показуємо CreateField
            ) : (
                <MainContent />  // Інакше показуємо основний контент
            )}
        </div>
    );
};

export default HomePage;
