import React, { useState } from 'react';
import { GoogleMap, LoadScript, Polygon, Marker, useGoogleMap } from '@react-google-maps/api';

const containerStyle = {
    width: '100%',
    height: '400px'
};

const center = {
    lat: 40.748817,
    lng: -73.985428
};

const MapPolygonPicker = ({ onPolygonComplete }) => {
    const [polygonCoords, setPolygonCoords] = useState([]);

    const handleMapClick = (event) => {
        const newCoords = [...polygonCoords, [event.latLng.lat(), event.latLng.lng()]];
        setPolygonCoords(newCoords);
        onPolygonComplete(newCoords);  // Відправляємо координати у батьківський компонент
    };

    const handlePolygonComplete = (polygon) => {
        const newCoords = polygon.getPath().getArray().map(latLng => [latLng.lat(), latLng.lng()]);
        setPolygonCoords(newCoords);
        onPolygonComplete(newCoords);  // Відправляємо координати у батьківський компонент
    };

    return (
        <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <GoogleMap
                mapContainerStyle={containerStyle}
                center={center}
                zoom={12}
                onClick={handleMapClick}
            >
                {polygonCoords.length > 0 && (
                    <Polygon
                        paths={polygonCoords}
                        options={{
                            fillColor: '#FF0000',
                            fillOpacity: 0.4,
                            strokeColor: '#FF0000',
                            strokeOpacity: 1,
                            strokeWeight: 2,
                        }}
                    />
                )}
            </GoogleMap>
        </LoadScript>
    );
};

export default MapPolygonPicker;
