import React, { useState } from 'react';
import DashboardContent from '../components/DashboardContent';
import MainContent from '../components/MainContent';
import CreateField from '../components/CreateField'; // Імпортуємо CreateField
import Navbar from '../components/Navbar';

const HomePage = () => {
    const [isCreatingField, setIsCreatingField] = useState(false);

    return (
        <div className="flex flex-col">
            <Navbar setIsCreatingField={setIsCreatingField} />
            <div className="flex">
                <DashboardContent />
                {isCreatingField ? (
                    <CreateField /> // Показуємо CreateField, якщо isCreatingField = true
                ) : (
                    <MainContent /> // Інакше показуємо MainContent
                )}
            </div>
        </div>
    );
}

export default HomePage;
