import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector'; // <-- 1. Імпортуйте детектор
import en from '../locales/en/translation.json';
import uk from '../locales/uk/translation.json';

i18n
    .use(LanguageDetector) // <-- 2. Використовуйте детектор (ПЕРЕД initReactI18next)
    .use(initReactI18next)
    .init({
        resources: {
            en: { translation: en },
            uk: { translation: uk },
        },
        // ВИДАЛІТЬ ЦЕЙ РЯДОК: lng: 'uk', // <-- 3. Більше не встановлюйте мову примусово тут

        fallbackLng: 'uk', // <-- Мова за замовчуванням/резервна, якщо детектор не знайде іншої
        interpolation: {
            escapeValue: false,
        },
        // 4. Налаштуйте детектор мови
        detection: {
            order: ['localStorage', 'navigator'], // <-- Вкажіть порядок джерел: спочатку localStorage, потім мова браузера
            caches: ['localStorage'],             // <-- Вкажіть, де зберігати визначену мову (щоб перемикач працював)
        },
        // Якщо використовуєте React.Suspense для завантаження перекладів, додайте:
        // react: {
        //   useSuspense: false, // або true
        // },
    });

export default i18n;
