// src/pages/AnalyticsPage.js
import React, { useState } from 'react'; // Додаємо useState
import DashboardContent from "../components/DashboardContent";
import AnalyticsContent from "../components/AnalyticsContent";
// Припускаємо, що FieldProvider обгортає AnalyticsPage десь вище

const AnalyticsPage = () => {
    // Стан для обраного поля, специфічний для сторінки аналітики
    const [selectedFieldId, setSelectedFieldId] = useState(null);

    // Функція, яка викликається при виборі поля в DashboardContent
    const handleFieldSelect = (fieldId) => {
        console.log(`Field ${fieldId} selected on Analytics Page`);
        setSelectedFieldId(fieldId); // Оновлюємо стан обраного поля
    };

    return (
        // FieldProvider має бути десь тут або вище в ієрархії
        // <FieldProvider>
        <div className="flex flex-wrap">
            {/* Передаємо обробник кліку та поточне обране поле в DashboardContent */}
            <DashboardContent
                onFieldSelect={handleFieldSelect}
                selectedFieldId={selectedFieldId} // Передаємо selectedFieldId для виділення
            />
            {/* Передаємо обране поле в AnalyticsContent */}
            <AnalyticsContent
                selectedFieldId={selectedFieldId}
            />
        </div>
        // </FieldProvider>
    );
}

export default AnalyticsPage;
