import React, { useState, useContext } from 'react';
import AuthForm from '../components/AuthForm';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';
import { signInWithPopup } from "firebase/auth";
import { auth, googleProvider } from "../firebase";
import { UserContext } from '../contexts/UserContext';
import LanguageSwitcher from '../components/LanguageSwitcher'; // <-- Імпортуємо перемикач


export default function RegisterPage() {
    const navigate = useNavigate();
    const { handleLoginSuccess } = useContext(UserContext); // Отримуємо handleLoginSuccess
    const [registerInProgress, setRegisterInProgress] = useState(false);
    const [formError, setFormError] = useState(null); // Додано стан для помилок форми


    const handleRegister = async ({ email, password, name }) => {
        setRegisterInProgress(true);
        setFormError(null); // Скидаємо попередні помилки
        console.log('RegisterPage: Attempting password registration with data:', { email, password, name });

        try {
            const res = await api.post('/auth/register/password', { email, password, name });
            console.log('Password registration success:', res.data);

            const { user } = res.data; // Отримуємо об'єкт user з відповіді бекенда

            // !!! ВИКЛИКАЄМО handleLoginSuccess для оновлення контексту та localStorage
            handleLoginSuccess(user);

            // Перенаправляємо на головну або іншу сторінку після успішної реєстрації та логіну
            navigate('/'); // Наприклад, на головну сторінку

        } catch (err) {
            console.error('Registration error:', err.response?.data || err.message);
            const errorMessage = err.response?.data?.message || 'Помилка реєстрації';
            setFormError(errorMessage); // Встановлюємо помилку форми
            alert('Помилка реєстрації: ' + errorMessage); // Також показуємо повідомлення про помилку
        } finally {
            setRegisterInProgress(false);
        }
    };


    // Ця функція ініціює Firebase popup
    const handleGoogleRegister = async () => {
        setRegisterInProgress(true);
        setFormError(null); // Скидаємо попередні помилки
        console.log('RegisterPage: Initiating Google sign-in popup for registration.');
        try {
            const result = await signInWithPopup(auth, googleProvider);
            const token = await result.user.getIdToken();

            console.log('RegisterPage: Sending Google token to backend /auth/register/google...');
            // Примітка: Ваш попередній код використовував /auth/login/google тут.
            // Якщо це реєстрація через Google, вам може знадобитися інший endpoint на бекенді, наприклад, /auth/register/google.
            const res = await api.post('/auth/register/google', { token }, { withCredentials: true });

            console.log('Google registration success:', res.data);
            const { user } = res.data; // Отримуємо об'єкт user з відповіді бекенда

            // !!! ВИКЛИКАЄМО handleLoginSuccess для оновлення контексту та localStorage
            handleLoginSuccess(user);

            // Перенаправляємо після успішної реєстрації через Google
            navigate('/'); // Наприклад, на головну сторінку

        } catch (error) {
            console.error('Google registration error:', error);
            const errorMessage = error.message || 'Помилка авторизації через Google';
            setFormError(errorMessage); // Встановлюємо помилку форми
            alert('Помилка авторизації через Google: ' + errorMessage); // Також показуємо повідомлення про помилку
        } finally {
            setRegisterInProgress(false);
        }
    };

    // Функція для переходу на сторінку логіну (використовується кнопкою перемикання)
    const switchToLogin = () => {
        navigate('/login');
    };


    return (
        // Використовуємо батьківський div з відносним позиціонуванням та центруванням
        <div className="relative min-h-screen flex flex-col items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
            {/* Перемикач мови, позиціонований абсолютно у верхньому правому куті */}
            <div className="absolute top-20 right-18 z-10"> {/* Додано z-10 */}
                <LanguageSwitcher />
            </div>

            {/* Контейнер для повідомлення про помилку та форми, вирівняний по центру */}
            <div className="max-w-sm w-full space-y-8">
                {/* Використовуємо клас Tailwind для стилізації помилки */}
                {formError && <div className="text-red-500 text-center mb-4">{formError}</div>}

                <AuthForm
                    type="register"
                    onSubmit={handleRegister}
                    onGoogleLogin={handleGoogleRegister}
                    isSubmitting={registerInProgress}
                    onSwitchType={switchToLogin} // Передаємо обробник для кнопки перемикання
                />
            </div>
        </div>
    );
}
