import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import en from '../locales/en/translation.json';
import uk from '../locales/uk/translation.json';

// --- Ручне читання мови з localStorage ---
const storedLang = localStorage.getItem('language');
// Встановлюємо мову за замовчуванням 'uk', якщо в localStorage нічого немає
const initialLang = storedLang || 'en';
// --- Кінець ручного читання ---


i18n.use(initReactI18next).init({
    resources: {
        en: { translation: en },
        uk: { translation: uk },
    },
    lng: initialLang, // <-- Використовуємо мову, яку прочитали вручну
    fallbackLng: 'en',
    interpolation: {
        escapeValue: false,
    },
    // Опції detection тут не потрібні, якщо ви не використовуєте LanguageDetector
});

export default i18n;
