import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import api from '../utils/api';

const Field = () => {
    const { id } = useParams();
    const [field, setField] = useState(null);
    const [points, setPoints] = useState([]);
    const [socket, setSocket] = useState(null);  // –°—Ç–µ–π—Ç –¥–ª—è WebSocket

    // 1. –°–ø–æ—á–∞—Ç–∫—É –≤—Å—Ç–∞–Ω–æ–≤–ª—é—î–º–æ —Ü–µ –ø–æ–ª–µ —è–∫ selected
    const selectField = async () => {
        try {
            await api.post('/fields/select/', { fieldId: id });
        } catch (err) {
            console.error('–ù–µ –≤–¥–∞–ª–æ—Å—è –≤–∏–±—Ä–∞—Ç–∏ –ø–æ–ª–µ:', err);
        }
    };

    const deselectField = async () => {
        try {
            await api.post('/fields/deselect/', { fieldId: id });
        } catch (err) {
            console.error('–ù–µ –≤–¥–∞–ª–æ—Å—è –¥–µ—Å–µ–ª–µ–∫—Ç–Ω—É—Ç–∏ –ø–æ–ª–µ:', err);
        }
    };


    const fetchPoints = async () => {
        try {
            const response = await api.get('/measurement-points/points');
            const newPoints = response.data.points;
            setPoints((prevPoints) => {
                if (JSON.stringify(prevPoints) !== JSON.stringify(newPoints)) {
                    return newPoints;
                }
                return prevPoints;
            });
        } catch (error) {
            console.error("Error fetching points:", error);
        }
    };

    const activatePoint = async (pointId) => {
        try {
            await api.post('/measurement-points/activate', { pointId });
            // !!! –ù–µ –º—ñ–Ω—è—î–º–æ –ª–æ–∫–∞–ª—å–Ω–∏–π state —Ç—É—Ç!
            // –ß–µ–∫–∞—î–º–æ WebSocket –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è!
        } catch (err) {
            console.error('–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –∞–∫—Ç–∏–≤–∞—Ü—ñ—ó —Ç–æ—á–∫–∏:', err);
        }
    };

    const deactivatePoint = async (pointId) => {
        try {
            await api.post('/measurement-points/deactivate', { pointId });
            // !!! –ù–µ –º—ñ–Ω—è—î–º–æ –ª–æ–∫–∞–ª—å–Ω–∏–π state —Ç—É—Ç!
            // –ß–µ–∫–∞—î–º–æ WebSocket –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è!
        } catch (err) {
            console.error('–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –¥–µ–∞–∫—Ç–∏–≤–∞—Ü—ñ—ó —Ç–æ—á–∫–∏:', err);
        }
    };


    useEffect(() => {
        const init = async () => {
            try {
                setPoints([]); // –û—á–∏—Å—Ç–∏—Ç–∏ —Ç–æ—á–∫–∏ –ø—Ä–∏ –∑–º—ñ–Ω—ñ –ø–æ–ª—è

                const fieldResponse = await api.get(`/fields/${id}`);
                setField(fieldResponse.data);

                await selectField();
                await fetchPoints(); // –ª–∏—à–µ –¥–ª—è –ø–µ—Ä–≤–∏–Ω–Ω–æ–≥–æ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è

            } catch (err) {
                console.error("–ü–æ–º–∏–ª–∫–∞ —ñ–Ω—ñ—Ü—ñ–∞–ª—ñ–∑–∞—Ü—ñ—ó –ø–æ–ª—è:", err);
            }
        };

        init();

        // üîΩ –¶–ï –í–ê–ñ–õ–ò–í–û: –ø—Ä–∏ –ø–µ—Ä–µ—Ö–æ–¥—ñ –Ω–∞ —ñ–Ω—à–µ –ø–æ–ª–µ ‚Äî –¥–µ—Å–µ–ª–µ–∫—Ç –ø–æ–ø–µ—Ä–µ–¥–Ω—å–æ–≥–æ
        return () => {
            deselectField();
        };
    }, [id]);



    useEffect(() => {
        const socket = new WebSocket('ws://localhost:5000');
        setSocket(socket);
        
        socket.onopen = () => {
            console.log('WebSocket –ø—ñ–¥–∫–ª—é—á–µ–Ω–æ');
            socket.send(JSON.stringify({ type: 'subscribe', fieldId: id }));
        };

        socket.onmessage = (event) => {
            console.log('–û—Ç—Ä–∏–º–∞–Ω–æ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è —á–µ—Ä–µ–∑ —Å–æ–∫–µ—Ç:', event.data);
            const data = JSON.parse(event.data);
            if (data.type === 'pointActivated') {
                setPoints((prev) =>
                    prev.map(p => p.id === data.pointId ? { ...p, active: true } : p)
                );
            }
            if (data.type === 'pointDeactivated') {
                setPoints((prev) =>
                    prev.map(p => p.id === data.pointId ? { ...p, active: false } : p)
                );
            }
        };



        socket.onclose = () => {
            console.log('WebSocket –∑–∞–∫—Ä–∏—Ç–æ');
        };

        return () => {
            socket.close();
        };
    }, [id]); // <-- –¥–æ–¥–∞–Ω–æ id –≤ –∑–∞–ª–µ–∂–Ω–æ—Å—Ç—ñ



    if (!field) return <div className="p-4">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</div>;

    return (
        <div className="p-6 w-full flex-1">
            <h1 className="text-3xl font-bold mb-2">{field.name}</h1>
            <p className="text-gray-600 mb-4">{field.description}</p>

            <div className="grid grid-cols-2 gap-4">
                <p><strong>–ü–ª–æ—â–∞:</strong> {field.area} –≥–∞</p>

                <p><strong>–°—Ç–≤–æ—Ä–µ–Ω–æ:</strong> {new Date(field.created_at).toLocaleDateString()}</p>

            </div>

            <hr className="my-6" />

            <h2 className="text-xl font-semibold mb-4 mt-6">–¢–æ—á–∫–∏ –ø–æ–ª—è</h2>

            <div className="max-h-96 overflow-y-auto pr-2 border rounded-lg shadow-sm bg-white">
                {points.length === 0 ? (
                    <p className="p-4 text-gray-500 italic">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ç–æ—á–æ–∫ –∞–±–æ —Ç–æ—á–æ–∫ –Ω–µ–º–∞—î.</p>
                ) : (
                    <ul className="divide-y divide-gray-200">
                        {points.map((point) => (
                            <PointItem
                                key={point.id}
                                point={point}
                                onActivate={activatePoint}
                                onDeactivate={deactivatePoint}
                            />
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤—ñ–¥–æ–±—Ä–∞–∂–µ–Ω–Ω—è –∫–æ–∂–Ω–æ—ó —Ç–æ—á–∫–∏
const PointItem = React.memo(({ point, onActivate, onDeactivate }) => {
    return (
        <li key={point.id} className="flex items-center px-4 py-3 hover:bg-gray-50">
            {/* Left column ‚Äî Point number */}
            <div className="h-6 px-3 flex-shrink-0 text-center text-l font-semibold text-gray-700 bg-gray-100 shadow-md rounded-md whitespace-nowrap">
                ‚Ññ{point.point_order}
            </div>

            {/* Vertical divider */}
            <div className="w-px h-16 bg-gray-300 mx-4"></div>

            {/* Main content */}
            <div className="flex-1">
                <div className="flex justify-between items-center ">
                    {/* Top: Latitude and Status */}
                    <div className="text-l text-gray-800">
                        lat: {point.latitude.toFixed(6)}
                    </div>
                    <div className="text-sm text-gray-800">
                        –°—Ç–∞—Ç—É—Å:{" "}
                        <span className={point.active ? "text-green-600" : "text-red-500"}>
                            {point.active ? "–ê–∫—Ç–∏–≤–Ω–∞" : "–ù–µ–∞–∫—Ç–∏–≤–Ω–∞"}
                        </span>
                    </div>
                    <button
                        onClick={() =>
                            point.active ? onDeactivate(point.id) : onActivate(point.id)
                        }
                        className={`text-sm px-3 py-1 rounded ${
                            point.active
                                ? "bg-red-100 text-red-600 hover:bg-red-200"
                                : "bg-green-100 text-green-600 hover:bg-green-200"
                        }`}
                    >
                        {point.active ? "–î–µ–∞–∫—Ç–∏–≤—É–≤–∞—Ç–∏" : "–ê–∫—Ç–∏–≤—É–≤–∞—Ç–∏"}
                    </button>
                </div>

                {/* Bottom: Longitude */}
                <div className="mt-1 text-sm text-gray-600">
                    lng: {point.longitude.toFixed(6)}
                </div>
            </div>
        </li>
    );
});

export default Field;
