// Card.jsx
export default function Card({ icon, value, label, color }) {
    return (
        <div className="w-1/2 lg:w-full">
            <div className="border-2 border-gray-400 border-dashed hover:border-transparent hover:bg-white hover:shadow-xl rounded p-6 m-2 md:mx-10 md:my-6">
                <div className="flex flex-col items-center">
                    <div className="flex-shrink pr-4">
                        <div className="rounded-full p-3 bg-gray-300">
                            <i className={`${icon} fa-fw fa-inverse ${color}`}></i>
                        </div>
                    </div>
                    <div className="flex-1">
                        <h3 className="font-bold text-3xl">{value}</h3>
                        <h5 className="font-bold text-gray-500">{label}</h5>
                    </div>
                </div>
            </div>
        </div>
    );
}
