import { useState } from "react";
import { useNavigate } from 'react-router-dom';
import UserMenu from "./UserMenu";

export default function Navbar({ setIsCreatingField }) {
    const [filter, setFilter] = useState("all");
    const [sort, setSort] = useState("asc");
    const [sortBy, setSortBy] = useState("name"); // Додаємо параметр для сортування (name, size, date)
    const [isFilterMenuOpen, setIsFilterMenuOpen] = useState(false);

    const navigate = useNavigate(); // використовуємо useNavigate для перенаправлення

    const handleAddField = () => {
        navigate('/create-field');
    };

    const handleSortToggle = () => {
        setSort((prev) => (prev === "asc" ? "desc" : "asc"));
        // TODO: додати логіку сортування на батьківському компоненті для сортування полів за назвою, розміром чи датою
    };

    const handleSortByChange = (event) => {
        setSortBy(event.target.value);
        // Скидаємо сортування на зростання при зміні параметра сортування
        setSort("asc");
    };

    const toggleFilterMenu = () => {
        setIsFilterMenuOpen((prev) => !prev);
    };

    return (
        <div className="h-40 lg:h-20 w-full flex flex-wrap">
            <nav className="bg-gray-200 w-full lg:max-w-sm flex items-center border-b border-gray-300 order-2 lg:order-1">
                <div className="flex w-full p-2 gap-2 items-center">
                    <button
                        onClick={handleAddField}
                        className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-full font-semibold"
                    >
                        <i className="fas fa-plus mr-1" />
                        Додати поле
                    </button>
                    <div className="flex gap-2 items-center">
                        {/* Вибір параметра для сортування */}
                        <select
                            value={sortBy}
                            onChange={handleSortByChange}
                            className="bg-gray-300 border-2 border-gray-200 rounded-full py-2 px-3 text-gray-700 font-semibold"
                        >
                            <option value="name">Назва</option>
                            <option value="size">Розмір</option>
                            <option value="date">Дата створення</option>
                            <button
                            onClick={handleSortToggle}
                            className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-3 py-2 rounded-full"
                            title="Змінити напрямок сортування"
                        >
                            {sort === "asc" ? (
                                <i className="fas fa-arrow-down" />
                            ) : (
                                <i className="fas fa-arrow-up" />
                            )}
                        </button>
                        </select>
                        {/* Кнопка для зміни напрямку сортування */}

                        {/* Меню фільтрації */}
                        <button
                            onClick={toggleFilterMenu}
                            className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-2 rounded-full"
                            title="Фільтрувати"
                        >
                            <i className="fas fa-filter" />
                        </button>
                        {isFilterMenuOpen && (
                            <div className="absolute bg-white shadow-md rounded-lg mt-2 p-2 w-48 border top-16 left-128">
                                <ul>
                                    <li>
                                        <button
                                            onClick={() => setFilter("all")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Усі
                                        </button>
                                    </li>
                                    <li>
                                        <button
                                            onClick={() => setFilter("active")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Активні
                                        </button>
                                    </li>
                                    <li>
                                        <button
                                            onClick={() => setFilter("archived")}
                                            className="w-full text-left px-4 py-2 hover:bg-gray-200"
                                        >
                                            Архів
                                        </button>
                                    </li>
                                </ul>
                            </div>
                        )}
                    </div>
                </div>
            </nav>

            <nav className="bg-gray-100 w-auto flex-1 border-b border-gray-300 order-1 lg:order-2">
                <div className="flex h-full justify-between items-center">
                    <div className="relative w-full max-w-3xl px-6">
                        <div className="absolute h-10 mt-1 left-0 top-0 flex items-center pl-10">
                            <i className="fas fa-search text-gray-600" />
                        </div>
                        <input
                            type="search"
                            placeholder="Пошук поля..."
                            className="block w-full bg-gray-200 text-gray-700 font-bold rounded-full pl-12 pr-4 py-3 focus:outline-none focus:bg-white focus:shadow-md"
                        />
                    </div>
                    <UserMenu />
                </div>
            </nav>
        </div>
    );
}
