import React, { useState } from "react";
import RegisterModal from "./RegisterModal";  // Імпортуємо RegisterModal для переходу на реєстрацію

const LoginModal = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [showRegister, setShowRegister] = useState(false);  // Стейт для контролю модалки реєстрації

    const closeModal = () => {
        setIsOpen(false);
        setShowRegister(false);  // Закриваємо реєстрацію при закритті входу
    };
    const openModal = () => setIsOpen(true);

    return (
        <>
            <button
                onClick={openModal}
                className="bg-blue-500 text-white px-6 py-3 rounded-md hover:bg-blue-600"
            >
                Вхід
            </button>

            {isOpen && !showRegister && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
                    <div className="bg-white w-96 p-6 rounded-md shadow-md relative">
                        <button
                            onClick={closeModal}
                            className="absolute top-2 right-2 text-gray-800 hover:text-gray-900 text-2xl"
                        >
                            &times;
                        </button>

                        <h2 className="text-xl font-semibold mb-4 text-center">Вхід</h2>
                        <form className="space-y-4">
                            <div>
                                <label className="block text-sm font-medium">Email</label>
                                <input
                                    type="email"
                                    className="w-full border border-gray-300 rounded-md p-2"
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium">Пароль</label>
                                <input
                                    type="password"
                                    className="w-full border border-gray-300 rounded-md p-2"
                                    required
                                />
                            </div>
                            <button
                                type="submit"
                                className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600"
                            >
                                Увійти
                            </button>
                        </form>
                        <div className="mt-4 text-center">
                            <p className="text-sm text-gray-500">
                                Не маєте акаунту?{" "}
                                <button
                                    onClick={() => setShowRegister(true)}
                                    className="text-blue-500 hover:underline"
                                >
                                    Реєструйтеся
                                </button>
                            </p>
                        </div>
                    </div>
                </div>
            )}

            {showRegister && (
                <RegisterModal closeModal={closeModal} />
                )}
        </>
    );
};

export default LoginModal;
