import React, { useContext, useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import { UserContext } from '../contexts/UserContext';
import api from '../utils/api';

const ProtectedRoute = ({ children }) => {
    const { user, isAdmin, hasDevice, loading } = useContext(UserContext);
    const [isTokenValid, setIsTokenValid] = useState(true);

    useEffect(() => {
        // Перевірка дійсності токена, якщо він є в localStorage
        const checkToken = async () => {
            try {
                const token = localStorage.getItem('token'); // Витягуємо токен із localStorage
                if (!token) {
                    setIsTokenValid(false);
                    return;
                }
                // Запит на сервер, щоб перевірити валідність токена
                const res = await api.post('/auth/verify-token', { token });
                if (!res.data.isValid) {
                    setIsTokenValid(false);
                }
            } catch (err) {
                setIsTokenValid(false);
            }
        };

        if (user) {
            checkToken();
        }
    }, [user]);

    if (loading) return <div>Завантаження...</div>; // Показуємо індикатор поки завантажуються дані

    // Якщо токен не дійсний або користувач не авторизований
    if (!isTokenValid || !user) {
        return <Navigate to="/login" replace />;
    }

    // Якщо користувач не має пристроїв, перенаправляємо на сторінку купівлі
    if (!hasDevice) {
        return <Navigate to="/buy-device" replace />;
    }

    // Якщо всі умови виконуються, дозволяємо доступ до сторінки
    return children;
};

export default ProtectedRoute;
