// src/components/AnalyticsContent.js
import React, { useEffect, useState } from 'react';
import api from '../utils/api';
// useFieldContext більше не потрібен тут

// Приймаємо пропс selectedFieldId
const AnalyticsContent = ({ selectedFieldId }) => {
    // ... (весь ваш існуючий код AnalyticsContent) ...

    // Ефект для завантаження даних при зміні selectedFieldId або timeRange
    useEffect(() => {
        if (!selectedFieldId) {
            setChartData(null);
            setError(null);
            setIsLoading(false);
            return;
        }

        // ТУТ МОЖНА ДОДАТИ selectField запит, якщо це потрібно робити саме при відображенні аналітики
        // Наприклад:
        // const selectAndFetch = async () => {
        //     try {
        //         await api.post('/fields/select/', { fieldId: selectedFieldId }); // Відправляємо select запит
        //         setIsLoading(true);
        //         setError(null);
        //         const apiUrl = `/field-measurements/chart/<span class="math-inline">\{selectedFieldId\}/</span>{timeRange}`;
        //         const response = await api.get(apiUrl);
        //         setChartData(response.data);
        //     } catch(err) {
        //         console.error('Error fetching chart data:', err);
        //         setError('Не вдалося завантажити дані графіка.');
        //         setChartData(null);
        //     } finally {
        //         setIsLoading(false);
        //     }
        // };
        // selectAndFetch();


        // Ваш існуючий код для завантаження даних графіка
        setIsLoading(true);
        setError(null);
        const apiUrl = `/field-measurements/chart/<span class="math-inline">\{selectedFieldId\}/</span>{timeRange}`;

        api.get(apiUrl)
            .then(response => {
                setChartData(response.data);
                setIsLoading(false);
            })
            .catch(err => {
                console.error('Error fetching chart data:', err);
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null);
                setIsLoading(false);
            });

        // ТУТ ТАКОЖ МОЖНА ДОДАТИ deselectField У Cleanup функцію, якщо select робиться тут
        // return () => {
        //     if (selectedFieldId) {
        //         // Викликати deselectField для selectedFieldId
        //         // Можливо, потрібно передати функцію deselect з батьківського компонента або використовувати контекст/стан
        //     }
        // };


    }, [selectedFieldId, timeRange]); // Залежності ефекту

    // ... (решта коду компонента AnalyticsContent) ...

    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-1 flex-wrap">
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl">
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}</h5>
                        </div>
                        <div className="p-5">
                            {/* Кнопки вибору масштабування */}
                            <div className="flex space-x-4 mb-4">
                                <button
                                    onClick={() => handleTimeRangeChange('7d')}
                                    className={`px-4 py-2 rounded ${timeRange === '7d' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Тиждень (7д)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('6m')}
                                    className={`px-4 py-2 rounded ${timeRange === '6m' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    6 місяців (6м)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('1y')}
                                    className={`px-4 py-2 rounded ${timeRange === '1y' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Рік (1р)
                                </button>
                            </div>

                            {/* Область для відображення графіка */}
                            <div className="ct-chart ct-golden-section" id="chart1">
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">Будь ласка, оберіть поле зі списку ліворуч, щоб побачити графік.</p>
                                )}
                                {selectedFieldId && isLoading && (
                                    <p className="text-center text-gray-500">Завантаження даних графіка...</p>
                                )}
                                {selectedFieldId && error && (
                                    <p className="text-center text-red-500">{error}</p>
                                )}
                                {selectedFieldId && !isLoading && !error && !chartData && (
                                    <p className="text-center text-gray-500">Дані для обраного поля за цей період не знайдені.</p>
                                )}
                            </div>
                        </div>

                        {/* Розділ таблиці (залишаємо як було) */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Table</h5>
                            </div>
                            <div className="p-5">
                                <table className="w-full p-5 text-gray-700">
                                    <thead>
                                    <tr>
                                        <th className="text-left text-blue-900">Name</th>
                                        <th className="text-left text-blue-900">Side</th>
                                        <th className="text-left text-blue-900">Role</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <td>Obi Wan Kenobi</td>
                                        <td>Light</td>
                                        <td>Jedi</td>
                                    </tr>
                                    <tr>
                                        <td>Greedo</td>
                                        <td>South</td>
                                        <td>Scumbag</td>
                                    </tr>
                                    <tr>
                                        <td>Darth Vader</td>
                                        <td>Dark</td>
                                        <td>Sith</td>
                                    </tr>
                                    </tbody>
                                </table>
                                <p className="py-2"><a href="#">See More issues...</a></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AnalyticsContent;
