import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminSensors = () => {
    const [sensors, setSensors] = useState([]);
    const [newSensorType, setNewSensorType] = useState('');
    const [newSensorRadius, setNewSensorRadius] = useState('');
    const [newSensorStatus, setNewSensorStatus] = useState('');
    const [newSensorUnit, setNewSensorUnit] = useState('');
    const [editSensorId, setEditSensorId] = useState(null);
    const [editSensorType, setEditSensorType] = useState('');
    const [editSensorRadius, setEditSensorRadius] = useState('');
    const [editSensorStatus, setEditSensorStatus] = useState('');
    const [editSensorUnit, setEditSensorUnit] = useState('');

    useEffect(() => {
        fetchSensors();
    }, []);

    const fetchSensors = async () => {
        try {
            const res = await axios.get('/api/sensors', { withCredentials: true });
            setSensors(res.data);
        } catch (err) {
            console.error('Error fetching sensors:', err);
        }
    };

    const handleAddSensor = async () => {
        try {
            await axios.post('/api/sensors',
                {
                    type: newSensorType,
                    radius: newSensorRadius,
                    status: newSensorStatus,
                    unit: newSensorUnit
                },
                { withCredentials: true }
            );
            setNewSensorType('');
            setNewSensorRadius('');
            setNewSensorStatus('');
            setNewSensorUnit('');
            fetchSensors();
        } catch (err) {
            console.error('Error adding sensor:', err);
        }
    };

    const handleUpdateSensor = async (id) => {
        try {
            await axios.put(`/api/sensors/${id}`,
                {
                    type: editSensorType,
                    radius: editSensorRadius,
                    status: editSensorStatus,
                    unit: editSensorUnit
                },
                { withCredentials: true }
            );
            setEditSensorId(null);
            setEditSensorType('');
            setEditSensorRadius('');
            setEditSensorStatus('');
            setEditSensorUnit('');
            fetchSensors();
        } catch (err) {
            console.error('Error updating sensor:', err);
        }
    };

    const handleDeleteSensor = async (id) => {
        try {
            await axios.delete(`/api/sensors/${id}`, { withCredentials: true });
            fetchSensors();
        } catch (err) {
            console.error('Error deleting sensor:', err);
        }
    };

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold mb-4">Управління сенсорами</h2>

            <div className="mb-6">
                <input
                    type="text"
                    placeholder="Тип сенсора"
                    value={newSensorType}
                    onChange={(e) => setNewSensorType(e.target.value)}
                    className="border px-4 py-2 mr-2"
                />
                <input
                    type="number"
                    placeholder="Радіус сенсора"
                    value={newSensorRadius}
                    onChange={(e) => setNewSensorRadius(e.target.value)}
                    className="border px-4 py-2 mr-2"
                />
                <input
                    type="text"
                    placeholder="Статус сенсора"
                    value={newSensorStatus}
                    onChange={(e) => setNewSensorStatus(e.target.value)}
                    className="border px-4 py-2 mr-2"
                />
                <select
                    value={newSensorUnit}
                    onChange={(e) => setNewSensorUnit(e.target.value)}
                    className="border px-4 py-2 mr-2"
                >
                    <option value="">Виберіть юніт</option>
                    <option value="°C">°C</option>
                    <option value="%">%</option>
                    <option value="pH">pH</option>
                </select>
                <button
                    onClick={handleAddSensor}
                    className="bg-indigo-500 text-white px-4 py-2 rounded hover:bg-indigo-600"
                >
                    Додати сенсор
                </button>
            </div>

            <table className="w-full table-auto border-collapse border border-gray-200">
                <thead>
                <tr className="bg-gray-100">
                    <th className="border px-4 py-2">ID</th>
                    <th className="border px-4 py-2">Тип</th>
                    <th className="border px-4 py-2">Радіус</th>
                    <th className="border px-4 py-2">Статус</th>
                    <th className="border px-4 py-2">Юніт</th>
                    <th className="border px-4 py-2">Дії</th>
                </tr>
                </thead>
                <tbody>
                {sensors.map((sensor) => (
                    <tr key={sensor.id}>
                        <td className="border px-4 py-2">{sensor.id}</td>
                        <td className="border px-4 py-2">
                            {editSensorId === sensor.id ? (
                                <input
                                    type="text"
                                    value={editSensorType}
                                    onChange={(e) => setEditSensorType(e.target.value)}
                                    className="border px-2 py-1"
                                />
                            ) : (
                                sensor.type
                            )}
                        </td>
                        <td className="border px-4 py-2">
                            {editSensorId === sensor.id ? (
                                <input
                                    type="number"
                                    value={editSensorRadius}
                                    onChange={(e) => setEditSensorRadius(e.target.value)}
                                    className="border px-2 py-1"
                                />
                            ) : (
                                sensor.radius
                            )}
                        </td>
                        <td className="border px-4 py-2">
                            {editSensorId === sensor.id ? (
                                <input
                                    type="text"
                                    value={editSensorStatus}
                                    onChange={(e) => setEditSensorStatus(e.target.value)}
                                    className="border px-2 py-1"
                                />
                            ) : (
                                sensor.status
                            )}
                        </td>
                        <td className="border px-4 py-2">
                            {editSensorId === sensor.id ? (
                                <select
                                    value={editSensorUnit}
                                    onChange={(e) => setEditSensorUnit(e.target.value)}
                                    className="border px-2 py-1"
                                >
                                    <option value="°C">°C</option>
                                    <option value="%">%</option>
                                    <option value="pH">pH</option>
                                </select>
                            ) : (
                                sensor.unit
                            )}
                        </td>
                        <td className="border px-4 py-2 space-x-2">
                            {editSensorId === sensor.id ? (
                                <button
                                    onClick={() => handleUpdateSensor(sensor.id)}
                                    className="bg-green-500 text-white px-2 py-1 rounded"
                                >
                                    Зберегти
                                </button>
                            ) : (
                                <button
                                    onClick={() => {
                                        setEditSensorId(sensor.id);
                                        setEditSensorType(sensor.type);
                                        setEditSensorRadius(sensor.radius);
                                        setEditSensorStatus(sensor.status);
                                        setEditSensorUnit(sensor.unit);
                                    }}
                                    className="bg-yellow-400 text-white px-2 py-1 rounded"
                                >
                                    Редагувати
                                </button>
                            )}
                            <button
                                onClick={() => handleDeleteSensor(sensor.id)}
                                className="bg-red-500 text-white px-2 py-1 rounded"
                            >
                                Видалити
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminSensors;
