import React, { useEffect, useState } from 'react';
import api from '../../utils/api';

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            setLoading(true);
            const res = await api.get('/users');
            setUsers(res.data);
        } catch (err) {
            setError('Не вдалося завантажити користувачів');
        } finally {
            setLoading(false);
        }
    };

    const fetchUserDetails = async (id) => {
        try {
            const res = await api.get(`/users/${id}`);
            setSelectedUser(res.data);
        } catch (err) {
            setError('Не вдалося отримати деталі користувача');
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Ви впевнені, що хочете видалити користувача?')) return;

        try {
            await api.delete(`/users/${id}`);
            setUsers(users.filter(user => user.id !== id));
            if (selectedUser?.id === id) setSelectedUser(null);
        } catch (err) {
            setError('Не вдалося видалити користувача');
        }
    };

    return (
        <div className="flex w-full">
            {/* Список користувачів */}
            <div className="w-1/2 p-4 border-r overflow-y-auto max-h-screen">
                <h2 className="text-xl font-bold mb-4">{t(`users`)}</h2>
                {loading && <p>Завантаження...</p>}
                {error && <p className="text-red-500">{error}</p>}
                {users.map(user => (
                    <div key={user.id} className="border p-3 rounded mb-2 flex justify-between items-center">
                        <div>
                            <p><strong>{user.name}</strong></p>
                            <p className="text-sm text-gray-500">{user.email}</p>
                        </div>
                        <div className="flex gap-2">
                            <button
                                onClick={() => fetchUserDetails(user.id)}
                                className="text-blue-500 hover:underline"
                            >
                                Деталі
                            </button>
                            <button
                                onClick={() => handleDelete(user.id)}
                                className="text-red-500 hover:underline"
                            >
                                Видалити
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {/* Деталі користувача */}
            <div className="w-1/2 p-4 overflow-y-auto max-h-screen">
                <h2 className="text-xl font-bold mb-4">Інформація про користувача</h2>
                {selectedUser ? (
                    <div className="space-y-2">
                        <p><strong>ID:</strong> {selectedUser.id}</p>
                        <p><strong>Ім’я:</strong> {selectedUser.name}</p>
                        <p><strong>Email:</strong> {selectedUser.email}</p>
                        <p><strong>Адмін:</strong> {selectedUser.is_admin ? 'Так' : 'Ні'}</p>
                    </div>
                ) : (
                    <p className="text-gray-500">Оберіть користувача для перегляду інформації</p>
                )}
            </div>
        </div>
    );
};

export default AdminUsers;
