import React, { useEffect, useState } from 'react';
import api from '../../utils/api';
import {useTranslation} from "react-i18next";

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null); // Зберігає ОБ'ЄКТ вибраного користувача
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { t } = useTranslation();

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            setLoading(true);
            setError(''); // Очищаємо помилки перед новим запитом
            const res = await api.get('/users');
            setUsers(res.data);
        } catch (err) {
            console.error('Failed to fetch users:', err); // Логуємо помилку
            setError('Не вдалося завантажити користувачів');
        } finally {
            setLoading(false);
        }
    };

    // Ця функція тепер викликатиметься при кліку на весь div користувача
    const fetchUserDetails = async (id) => {
        // Перевіряємо, чи вже обрано цього користувача, щоб уникнути зайвих запитів
        if (selectedUser?.id === id) {
            console.log(`User ${id} already selected, skipping fetch.`);
            return;
        }

        try {
            setError(''); // Очищаємо помилки перед новим запитом
            const res = await api.get(`/users/${id}`);
            setSelectedUser(res.data); // Встановлюємо весь об'єкт користувача
        } catch (err) {
            console.error(`Failed to fetch details for user ${id}:`, err); // Логуємо помилку
            setError('Не вдалося отримати деталі користувача');
            setSelectedUser(null); // Скидаємо вибраного користувача у разі помилки
        }
    };

    const handleDelete = async (id) => {
        // Додано переклад до повідомлення підтвердження
        if (!window.confirm('Ви впевнені, що хочете видалити користувача?')) return;

        try {
            setError(''); // Очищаємо помилки перед новим запитом
            await api.delete(`/users/${id}`);
            // Видаляємо користувача зі списку в стані
            setUsers(users.filter(user => user.id !== id));
            // Якщо видалили користувача, який був вибраний, скидаємо selectedUser
            if (selectedUser?.id === id) {
                setSelectedUser(null);
            }
            console.log(`User ${id} deleted successfully.`);
        } catch (err) {
            console.error(`Failed to delete user ${id}:`, err); // Логуємо помилку
            setError('Не вдалося видалити користувача');
        }
    };

    return (
        // Прибираємо outer flex-контейнер та праву колонку. Залишаємо контейнер для списку
        <div className="p-4 overflow-y-auto max-h-screen w-full"> {/* adjusted classes */}
            <h2 className="text-xl font-bold mb-4">{t(`users`)}</h2>
            {loading && <p>{t(`loading`)}</p>}
            {/* Відображаємо помилку, якщо є */}
            {error && <p className="text-red-500">{error}</p>}

            {/* Перевіряємо, чи є користувачі для відображення */}
            {!loading && !error && users.length === 0 && (
                <p className="text-gray-500">{t('no_users_found')}</p>
            )}

            {/* Мапуємо користувачів та рендеримо деталі під обраним */}
            {users.map(user => (
                // Використовуємо Fragment, оскільки повертаємо два елементи (div користувача та div деталей)
                <React.Fragment key={user.id}>
                    {/* Div елемента списку користувача */}
                    <div
                        // Використовуємо нову функцію handleUserClick
                        onClick={() => handleUserClick(user)}
                        // Класи для підсвічування та стилю курсора залишаємо як було
                        className={`border p-3 rounded mb-2 flex justify-between items-center cursor-pointer transition-colors duration-150
                            ${selectedUser?.id === user.id
                            ? 'bg-blue-100 border-blue-500' // Класи для вибраного стану
                            : 'border-gray-300 hover:bg-gray-100' // Класи для невибраного стану
                        }`}
                    >
                        <div>
                            <p><strong>{user.name}</strong></p>
                            <p className="text-sm text-gray-500">{user.email}</p>
                        </div>
                        <div className="flex gap-2">
                            {/* Кнопка "Видалити" залишається, з e.stopPropagation() */}
                            <button
                                onClick={(e) => {
                                    e.stopPropagation(); // Зупиняємо поширення кліку
                                    handleDelete(user.id);
                                }}
                                className="text-red-500 hover:underline"
                            >
                                {t(`delete`)}
                            </button>
                        </div>
                    </div>

                    {/* Умовно рендеримо деталі КОНКРЕТНОГО користувача, якщо він вибраний */}
                    {selectedUser?.id === user.id && (
                        // Додаємо стилі для блоку деталей (фон, відступи, тінь, відступи зліва/справа, нижній відступ)
                        <div className="bg-white p-4 rounded shadow mb-4 ml-4 mr-4">
                            {/* Додаємо заголовок для блоку деталей з перекладом */}
                            <h3 className="text-lg font-semibold mb-2">{t(`user_information`)}</h3>
                            <div className="space-y-2">
                                <p><strong>ID:</strong> {selectedUser.id}</p>
                                {/* Використовуємо перекладені підписи полів */}
                                <p><strong>{t(`first_name`)}:</strong> {selectedUser.name}</p>
                                <p><strong>Email:</strong> {selectedUser.email}</p>
                                <p><strong>{t(`admin`)}:</strong> {selectedUser.is_admin ? t(`yes`) : t(`no`)}</p>
                                {selectedUser.profile_picture_url && (
                                    <p><strong>{t(`profile_picture`)}:</strong> <img src={selectedUser.profile_picture_url} alt="Profile" className="w-10 h-10 rounded-full inline-block ml-2" /></p>
                                )}
                                {/* Додайте інші поля деталей користувача тут */}
                            </div>
                            {/* Можливо, захочете додати кнопку "Приховати деталі" */}
                            {/* <button onClick={() => setSelectedUser(null)} className="mt-4 text-blue-500 hover:underline">{t('close_details')}</button> */}
                        </div>
                    )}
                </React.Fragment> // <-- Кінець Fragment
            ))}
        </div>
        // ВИДАЛЕНО: окремий div для правої колонки з деталями
    );
};

export default AdminUsers;
