import React, { useEffect, useState } from 'react';
import api from '../utils/api';
// Імпортуємо UserContext, якщо хочемо оновлювати його після збереження
import { useUserContext } from '../contexts/UserContext';

const Profile = () => {
    // Використовуємо локальний стан для даних користувача на цій сторінці
    const [user, setUser] = useState(null);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    // hasPassword можна отримати з об'єкта user, якщо бекенд його повертає
    const [hasPassword, setHasPassword] = useState(false);

    // !!! ВИПРАВЛЕНО: Змінна стану для ЗБЕРІГАННЯ ФАЙЛУ, обраного для завантаження !!!
    const [selectedProfilePictureFile, setSelectedProfilePictureFile] = useState(null);

    const [currentPassword, setCurrentPassword] = useState("");
    // !!! ВИПРАВЛЕНО: Уникаємо конфлікту імен з функціями useState !!!
    const [newPassword, setNewPasswordState] = useState("");
    const [confirmPassword, setConfirmPasswordState] = useState("");

    const [message, setMessage] = useState("");
    const [isLoading, setIsLoading] = useState(true); // Стан завантаження даних профілю


    // Отримуємо функцію для оновлення контексту, якщо потрібно оновити його після збереження
    const { handleLoginSuccess } = useUserContext();


    // Валідація форми перед збереженням
    const validateForm = () => {
        setMessage(""); // Очищаємо попередні повідомлення
        if (newPassword || confirmPassword || currentPassword) { // Перевіряємо, якщо хоч одне поле пароля заповнено
            if (!currentPassword) {
                setMessage("Поточний пароль обов'язковий для зміни пароля.");
                return false;
            }
            if (newPassword !== confirmPassword) {
                setMessage("Новий пароль не співпадає з підтвердженням.");
                return false;
            }
            // Можна додати перевірку на складність нового пароля
        }

        return true;
    };


    // Ефект для завантаження даних користувача при монтуванні
    useEffect(() => {
        const fetchUser = async () => {
            setIsLoading(true); // Починаємо завантаження
            try {
                // Викликаємо ендпоінт для отримання даних поточного користувача (/users/me)
                // Бекенд має повертати об'єкт user з усіма полями, включаючи profile_picture_url
                const res = await api.get(`/users/me`);
                const userData = res.data; // Отримуємо об'єкт користувача з відповіді

                console.log("Profile: User data fetched:", userData); // Лог отриманих даних

                // !!! ВИПРАВЛЕНО: Встановлюємо ВЕСЬ об'єкт користувача у локальний стан !!!
                setUser(userData);
                setName(userData.name || ""); // Встановлюємо ім'я (забезпечуємо рядок)
                setEmail(userData.email || ""); // Встановлюємо email
                // Перевіряємо наявність поля password у відповіді, щоб визначити, чи є пароль
                setHasPassword(!!userData.password); // !! (перетворює на булеве)

                // Змінна стану для файлу selectedProfilePictureFile залишається null,
                // вона не повинна ініціалізуватися з URL!

            } catch (error) {
                console.error("Помилка завантаження профілю:", error.response?.data || error.message || error);
                setMessage(error.response?.data?.message || "Помилка завантаження профілю.");
            } finally {
                setIsLoading(false); // Завершуємо завантаження
            }
        };

        fetchUser();
    }, []); // Залежності порожні - ефект виконується один раз при монтуванні


    // Обробник збереження змін
    const handleSave = async (e) => {
        e.preventDefault();
        if (!validateForm()) return; // Валідуємо форму
        // Перевіряємо, чи дані користувача вже завантажені
        if (!user || !user.id) {
            setMessage("Помилка: Дані користувача не завантажено для збереження.");
            return;
        }


        try {
            const formData = new FormData(); // Використовуємо FormData для відправки файлу
            formData.append("name", name); // Додаємо ім'я
            // formData.append("email", email); // Додайте, якщо дозволяєте змінювати email

            // !!! ВИПРАВЛЕНО: Додаємо обраний файл під ключем, який очікує бекенд !!!
            // "profilePictureFile" - це ПРИКЛАД ключа. Замініть його на той, який використовує Multer.
            if (selectedProfilePictureFile) {
                console.log("Appending file to FormData:", selectedProfilePictureFile);
                formData.append("profilePictureFile", selectedProfilePictureFile); // Додаємо ФАЙЛ
            }

            // Додаємо поля пароля лише якщо вони заповнені
            if (currentPassword) formData.append("currentPassword", currentPassword);
            // Використовуємо змінені назви змінних стану для паролів
            if (newPassword) formData.append("newPassword", newPassword);
            if (confirmPassword) formData.append("confirmPassword", confirmPassword);


            // Викликаємо ендпоінт оновлення користувача з FormData
            const res = await api.put(`/users/${user.id}`, formData, {
                headers: { "Content-Type": "multipart/form-data" }, // Вказуємо тип контенту для FormData
            });

            const updatedUserData = res.data; // Отримуємо ОНОВЛЕНИЙ об'єкт користувача з відповіді бекенду
            console.log("Profile: User updated successfully:", updatedUserData);

            // Оновлюємо локальний стан користувача на цій сторінці свіжими даними
            setUser(updatedUserData);
            setMessage("Зміни успішно збережено.");

            // !!! ВИПРАВЛЕНО: ОНОВЛЮЄМО UserContext після успішного збереження !!!
            // Це оновить дані користувача у всьому застосунку (зокрема, в UserMenu)
            handleLoginSuccess(updatedUserData); // Передаємо оновлені дані користувача


            // Очищаємо поля паролів та стан обраного файлу після успішного збереження
            setCurrentPassword("");
            setNewPasswordState("");
            setConfirmPasswordState("");
            setSelectedProfilePictureFile(null); // Скидаємо обраний файл

        } catch (err) {
            console.error("Profile: Error saving profile:", err.response?.data || err.message || err);
            setMessage(err.response?.data?.message || "Помилка збереження змін.");
        }
    };


    // Допоміжні обробники зміни полів (для чистоти коду)
    const handleNameChange = (e) => { setName(e.target.value); setMessage(""); };
    const handleCurrentPasswordChange = (e) => { setCurrentPassword(e.target.value); setMessage(""); };
    const handleNewPasswordChange = (e) => { setNewPasswordState(e.target.value); setMessage(""); };
    const handleConfirmPasswordChange = (e) => { setConfirmPasswordState(e.target.value); setMessage(""); };
    // Обробник вибору файлу
    const handleFileSelect = (e) => {
        const file = e.target.files ? e.target.files[0] : null;
        setSelectedProfilePictureFile(file); // Встановлюємо обраний файл
        setMessage(""); // Очищаємо повідомлення
        // TODO: Можна додати попередній перегляд фотографії перед завантаженням,
        // використовуючи URL.createObjectURL(file) та useEffect для очищення
    }


    // Логіка відображення під час завантаження або якщо користувач не завантажений
    if (isLoading || !user) {
        return <p>Завантаження профілю...</p>; // Показуємо повідомлення завантаження
    }


    return (
        <div className="w-full md:w-2/3 p-6">
            <h2 className="text-2xl font-semibold mb-4">Мій акаунт</h2>

            <form onSubmit={handleSave} className="space-y-4">
                <div>
                    <label className="block font-medium text-gray-700 mb-1">Фото профілю</label>
                    {/* Поле для вибору файлу */}
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleFileSelect} // Використовуємо обробник вибору файлу
                        className="w-full"
                    />

                    {/* Відображення ПОТОЧНОГО фото профілю (якщо URL існує на об'єкті user) */}
                    {user.profile_picture_url && (
                        <div className="mt-2"> {/* Додаємо відступ */}
                            <p className="text-sm text-gray-600 mb-1">Поточне фото:</p> {/* Додаємо підпис */}
                            <img
                                src={user.profile_picture_url} // Використовуємо URL з об'єкта user
                                alt="Поточне фото профілю"
                                className="w-24 h-24 rounded-full object-cover border border-gray-300" // Стилі
                            />
                            {/* TODO: Додати кнопку "Видалити фото" */}
                        </div>
                    )}

                    {/* Опціонально: Відображення попереднього перегляду НОВОГО обраного файлу перед завантаженням */}
                    {selectedProfilePictureFile && (
                        <div className="mt-2"> {/* Додаємо відступ */}
                            <p className="text-sm text-gray-600 mb-1">Попередній перегляд нового фото:</p> {/* Додаємо підпис */}
                            {/* Створюємо тимчасовий URL для обраного файлу */}
                            <img
                                src={URL.createObjectURL(selectedProfilePictureFile)}
                                alt="Попередній перегляд нового фото"
                                className="w-24 h-24 rounded-full object-cover border border-blue-500" // Інший стиль рамки для візуального розрізнення
                            />
                            {/* TODO: Додати очищення тимчасового URL в useEffect cleanup */}
                        </div>
                    )}

                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Email</label>
                    {/* Поле email, ймовірно, не змінюється */}
                    <input
                        type="email"
                        value={email}
                        disabled
                        className="w-full px-4 py-2 border border-gray-300 rounded bg-gray-100 cursor-not-allowed"
                    />
                </div>

                <div>
                    <label className="block font-medium text-gray-700 mb-1">Ім’я</label>
                    <input
                        type="text"
                        value={name}
                        onChange={handleNameChange} // Використовуємо обробник
                        className="w-full px-4 py-2 border border-gray-300 rounded"
                    />
                </div>

                {/* Секція зміни пароля - відображається, якщо у користувача є пароль */}
                {/* Використовуємо user?.password для перевірки наявності поля password на об'єкті user */}
                {user?.password && (
                    <>
                        <hr className="my-4" />
                        <h3 className="text-lg font-semibold">Зміна пароля</h3>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Поточний пароль</label>
                            <input
                                type="password"
                                value={currentPassword}
                                onChange={handleCurrentPasswordChange} // Використовуємо обробник
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Новий пароль</label>
                            <input
                                type="password"
                                value={newPassword}
                                onChange={handleNewPasswordChange} // Використовуємо обробник
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>

                        <div>
                            <label className="block font-medium text-gray-700 mb-1">Підтвердження пароля</label>
                            <input
                                type="password"
                                value={confirmPassword}
                                onChange={handleConfirmPasswordChange} // Використовуємо обробник
                                className="w-full px-4 py-2 border border-gray-300 rounded"
                            />
                        </div>
                    </>
                )}
                {/* TODO: Реалізувати секцію для встановлення пароля для користувачів без пароля (наприклад, Google Auth) */}


                {message && <p className="text-sm text-blue-600">{message}</p>}

                <button
                    type="submit"
                    className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700 transition"
                >
                    Зберегти зміни
                </button>
            </form>
        </div>
    );
};

export default Profile;
