import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function UserMenu() {
    const [userName, setUserName] = useState('');
    const [isMenuVisible, setIsMenuVisible] = useState(false);

    useEffect(() => {
        // Отримуємо ім'я користувача за допомогою токену з кукі
        const fetchUserName = async () => {
            try {
                const response = await axios.get('/api/user');  // Запит до API для отримання даних користувача
                setUserName(response.data.name);  // Встановлюємо ім'я користувача
            } catch (error) {
                console.error('Error fetching user:', error);
            }
        };

        fetchUserName();
    }, []);

    const toggleMenu = () => {
        setIsMenuVisible(!isMenuVisible);  // Перемикаємо видимість меню
    };

    return (
        <div className="flex relative inline-block pr-6">
            <div className="relative text-sm">
                <button className="flex items-center focus:outline-none mr-3" onClick={toggleMenu}>
                    <span className="hidden md:inline-block">Hi, {userName || 'User'}</span>
                    <svg className="pl-2 h-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 129 129">
                        <path d="m121.3,34.6c-1.6-1.6-4.2-1.6-5.8,0l-51,51.1-51.1-51.1c-1.6-1.6-4.2-1.6-5.8,0-1.6,1.6-1.6,4.2 0,5.8l53.9,53.9c0.8,0.8 1.8,1.2 2.9,1.2 1,0 2.1-0.4 2.9-1.2l53.9-53.9c1.7-1.6 1.7-4.2 0.1-5.8z"/>
                    </svg>
                </button>

                {isMenuVisible && (
                    <div className="bg-white nunito rounded shadow-md mt-2 absolute mt-12 top-0 right-0 min-w-full z-30">
                        <ul className="list-reset">
                            <li><a href="#" className="px-4 py-2 block text-gray-900 hover:bg-indigo-400 hover:text-white">My account</a></li>
                            <li><a href="#" className="px-4 py-2 block text-gray-900 hover:bg-indigo-400 hover:text-white">Notifications</a></li>
                            <li><hr className="border-t mx-2 border-gray-400" /></li>
                            <li><a href="#" className="px-4 py-2 block text-gray-900 hover:bg-indigo-400 hover:text-white">Logout</a></li>
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}
