import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import api from '../utils/api';

const Field = () => {
    const { id } = useParams();
    const [field, setField] = useState(null);
    const [points, setPoints] = useState([]);

    // Функція для отримання точок поля
    const fetchPoints = () => {
        api.get(`/measurement-points/${id}/points`)
            .then(res => setPoints(res.data))
            .catch(err => console.error('Помилка при отриманні точок:', err));
    };

    // Функція для активації точки
    const activatePoint = (pointId) => {
        api.post('/activate', { pointId })
            .then(() => fetchPoints())  // Оновлюємо точки після активації
            .catch(err => console.error('Помилка при активації точки:', err));
    };

    // Функція для деактивації точки
    const deactivatePoint = (pointId) => {
        api.post('/deactivate', { pointId })
            .then(() => fetchPoints())  // Оновлюємо точки після деактивації
            .catch(err => console.error('Помилка при деактивації точки:', err));
    };

    useEffect(() => {
        api.get(`/fields/${id}`)
            .then(res => setField(res.data))
            .catch(err => console.error(err));

        fetchPoints();  // Оновлюємо точки при завантаженні сторінки

        // Оновлюємо точки кожну хвилину
        const interval = setInterval(fetchPoints, 60000);
        return () => clearInterval(interval);  // Очищаємо інтервал при демонтажі компонента
    }, [id]);

    if (!field) return <div className="p-4">Завантаження...</div>;

    return (
        <div className="p-6 w-full flex-1">
            <h1 className="text-3xl font-bold mb-2">{field.name}</h1>
            <p className="text-gray-600 mb-4">{field.description}</p>

            <div className="grid grid-cols-2 gap-4">
                <p><strong>Площа:</strong> {field.area} га</p>
                <p><strong>Тип ґрунту:</strong> {field.soilType}</p>
                <p><strong>Створено:</strong> {field.created_at}</p>
                <p><strong>Оновлено:</strong> {new Date(field.updatedAt).toLocaleDateString()}</p>
            </div>

            <hr className="my-6" />

            <h2 className="text-xl font-semibold mb-2">Останнє вимірювання</h2>
            {field.lastMeasurement ? (
                <ul className="list-disc ml-6 text-gray-700">
                    <li>Температура: {field.lastMeasurement.temperature} °C</li>
                    <li>Вологість: {field.lastMeasurement.humidity} %</li>
                    <li>Кислотність: {field.lastMeasurement.ph}</li>
                    <li>Дата: {new Date(field.lastMeasurement.date).toLocaleString()}</li>
                </ul>
            ) : (
                <p className="text-gray-500">Немає вимірювань</p>
            )}

            <hr className="my-6" />

            <h2 className="text-xl font-semibold mb-2">Точки поля</h2>
            <ul className="list-disc ml-6">
                {points.length === 0 ? (
                    <p>Немає точок для цього поля.</p>
                ) : (
                    points.map((point) => (
                        <li key={point.id} className="flex justify-between items-center">
                            <span>{point.name} - Статус: {point.active ? 'Активна' : 'Неактивна'}</span>
                            <div>
                                {point.active ? (
                                    <button onClick={() => deactivatePoint(point.id)} className="text-red-500">
                                        Деактивувати
                                    </button>
                                ) : (
                                    <button onClick={() => activatePoint(point.id)} className="text-green-500">
                                        Активувати
                                    </button>
                                )}
                            </div>
                        </li>
                    ))
                )}
            </ul>
        </div>
    );
};

export default Field;
