import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import api from "../utils/api";
import MapPolygonPicker from './MapPolygonPicker'; // імпорт компонента мапи

const CreateField = () => {
    const navigate = useNavigate();
    const { id } = useParams(); // отримуємо id з URL

    const [name, setName] = useState('');
    const [area, setArea] = useState('');
    const [geoZone, setGeoZone] = useState(null);
    const [manualCoords, setManualCoords] = useState('');

    useEffect(() => {
        if (id) {
            // якщо є ID — редагування
            api.get(`/fields/${id}`).then(res => {
                const field = res.data;
                setName(field.name);
                setArea(field.area);
                if (field.geo_zone?.coordinates?.[0]) {
                    let coords = field.geo_zone.coordinates[0];

                    // Якщо остання точка дублює першу — прибираємо її
                    if (coords.length > 1 &&
                        coords[0][0] === coords[coords.length - 1][0] &&
                        coords[0][1] === coords[coords.length - 1][1]
                    ) {
                        coords = coords.slice(0, -1);
                    }

                    setGeoZone(coords); // ✅ це залишаємо
                    setManualCoords(coords
                        .map(pair => `[${pair[0]}, ${pair[1]}]`)
                        .join(',\n'));
                }



            }).catch(err => {
                console.error("Помилка при завантаженні поля", err);
                alert("Не вдалося завантажити поле");
            });
        }
        else {
            // Якщо ми НЕ редагуємо, а створюємо нове поле — скидаємо все
            setName('');
            setArea('');
            setGeoZone(null);
            setManualCoords('');
        }
    }, [id]);

    useEffect(() => {
        if (geoZone && geoZone.length >= 3) {
            const hectares = calculatePolygonArea(geoZone);
            setArea(hectares.toFixed(3)); // округлюємо до 2 знаків
        }
    }, [geoZone]);

    const handleCancel = () => {
        navigate('/home');

    };

    const parseManualCoords = () => {
        try {
            // Очистити від пробілів, переводимо в масив
            const cleaned = manualCoords
                .replace(/\s+/g, '') // прибирає пробіли/переноси
                .replace(/\],\[/g, ']~[') // розділяємо між парами
                .split('~')
                .map(pair => JSON.parse(pair)); // парсимо кожну пару

            if (!Array.isArray(cleaned) || cleaned.length < 3) throw new Error("Недостатньо координат");
            return cleaned;
        } catch (e) {
            alert("Невірний формат координат. Перевірте, чи правильно ви вказали всі дужки та коми.");
            return null;
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();

        // Використовуємо координати з мапи або ручного вводу
        let coordinates = parseManualCoords();


        if (!coordinates) {
            alert("Будь ласка, позначте поле на карті або введіть координати вручну.");
            return;
        }

        // Створюємо змінні для першої та останньої точки
        const firstPoint = coordinates[0];
        const lastPoint = coordinates[coordinates.length - 1];

        // Перевіряємо, чи перша та остання точки однакові
        const isClosed = firstPoint[0] === lastPoint[0] && firstPoint[1] === lastPoint[1];

        // Якщо полігон не замкнений, додаємо першу точку в кінець
        if (!isClosed) {
            coordinates.push(firstPoint);
        }

        const payload = {
            name,
            area: parseFloat(area),
            geo_zone: {
                type: "Polygon",
                coordinates: [coordinates],
            },
        };

        try {
            if (id) {
                await api.put(`/fields/${id}`, payload);
                alert("Поле оновлено!");
            } else {
                await api.post('/fields', payload);
                alert("Поле успішно створене!");
            }
            navigate("/home");
        } catch (error) {
            console.error("Помилка:", error);
            alert(error?.response?.data?.error || "Щось пішло не так");
        }
    };


    const calculatePolygonArea = (coords) => {
        if (!window.google || !coords || coords.length < 3) return 0;

        const path = coords.map(([lat, lng]) => new window.google.maps.LatLng(lat, lng));
        const areaMetersSquared = window.google.maps.geometry.spherical.computeArea(path);
        const SQUARE_METERS_IN_HECTARE = 10000;
        const areaHectares = areaMetersSquared / SQUARE_METERS_IN_HECTARE;
        return areaHectares;
    };

    const handleDelete = async () => {
        if (!id) return;

        const confirmed = window.confirm("Ви впевнені, що хочете видалити це поле?");
        if (!confirmed) return;

        try {
            await api.delete(`/fields/${id}`);
            alert("Поле успішно видалено!");
            navigate('/home');
        } catch (error) {
            console.error("Помилка при видаленні:", error);
            alert(error?.response?.data?.message || "Не вдалося видалити поле");
        }
    };

    return (
        <div className="w-full flex-1">
            <div className="p-6">
                <h5 className="font-bold text-black text-xl mb-4">{id ? "Редагувати поле" : "Створити нове поле"}</h5>
                <form onSubmit={handleSubmit}>
                    <label className="block mb-4">
                        Назва поля:
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                        />
                    </label>
                    <label className="block mb-4">
                        Площа поля (в га):
                        <input
                            type="number"
                            value={area}
                            onChange={(e) => setArea(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                            step="0.01"
                        />
                    </label>

                    {/* Карта */}
                    <div className="my-6">
                        <h6 className="font-semibold mb-2">Позначте поле на карті:</h6>
                        <div className="h-80 border rounded-lg flex items-center justify-center text-gray-500">
                            <MapPolygonPicker
                                onPolygonComplete={(coords) => {
                                    setGeoZone(coords);
                                    const hectares = calculatePolygonArea(coords);
                                    setArea(hectares.toFixed(3));
                                }}
                                setManualCoordsFromMap={(coordsText) => setManualCoords(coordsText)}
                                initialPolygon={geoZone}
                                resetSignal={id}
                            />



                        </div>
                    </div>

                    {/* Ручний ввід координат */}
                    <div className="my-6">
                        <h6 className="font-semibold mb-2">Або введіть координати тут:</h6>
                        <textarea
                            rows="6"
                            value={manualCoords}
                            onChange={(e) => setManualCoords(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg font-mono text-sm"
                            placeholder={`[40.7484, -73.9857],\n[40.7488, -73.9855],\n[40.7486, -73.9842],\n[40.7482, -73.9844],\n[40.7484, -73.9857]`}
                        />
                        <p className="text-xs text-gray-500 mt-2">
                            Введіть координати у форматі: <code>[широта, довгота]</code>, кожна пара з нового рядка. Має утворювати замкнений полігон.
                        </p>
                    </div>

                    <div className="flex flex-wrap gap-4 mt-6">
                        <button
                            type="submit"
                            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full"
                        >
                            {id ? "Редагувати поле" : "Створити нове поле"}
                        </button>

                        <button
                            type="button"
                            onClick={handleCancel}
                            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full font-semibold"
                        >
                            Скасувати
                        </button>


                    {id && (
                        <button
                            type="button"
                            onClick={handleDelete}
                            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full font-semibold shadow-lg"
                        >
                            Видалити поле
                        </button>
                    )}
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreateField;
