import React from 'react';

const categories = [
    { id: 'users', label: 'Користувачі', icon: 'fa-user' },
    { id: 'devices', label: 'Пристрої', icon: 'fa-microchip' },
    { id: 'sensors', label: 'Датчики', icon: 'fa-thermometer-half' },
    { id: 'userDevices', label: 'Датчики користувачів', icon: 'fa-link' },
];

const AdminDashboard = ({ onSelectCategory }) => {
    return (
        <div className="bg-white w-full lg:max-w-sm p-4 border-r h-screen overflow-y-auto">
            <h2 className="text-2xl font-bold mb-4">Категорії</h2>
            <div className="flex flex-col gap-4">
                {categories.map(cat => (
                    <div
                        key={cat.id}
                        onClick={() => onSelectCategory(cat.id)}
                        className="cursor-pointer border border-gray-300 rounded-lg p-4 hover:bg-indigo-100 transition"
                    >
                        <div className="flex items-center gap-3">
                            <i className={`fas ${cat.icon} text-indigo-500`}></i>
                            <span className="font-medium">{cat.label}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminDashboard;
