// src/components/DashboardContent.jsx
import { useState, useEffect } from 'react';
import { useFieldContext } from '../contexts/FieldContext';
import api from '../utils/api'; // –ø—Ä–∏–ø—É—Å–∫–∞—î–º–æ, —â–æ api –Ω–∞–ª–∞—à—Ç–æ–≤–∞–Ω–æ

export default function DashboardContent() {
    const { filter, sortBy, sortOrder } = useFieldContext();
    const [fields, setFields] = useState([]);

    useEffect(() => {
        api.get('/fields')
            .then(response => setFields(response.data))
            .catch(error => console.error('Error fetching fields:', error));
    }, []);

    // üîç –§—ñ–ª—å—Ç—Ä–∞—Ü—ñ—è
    const filteredFields = fields.filter(field => {
        if (filter === "all") return true;
        if (filter === "active") return field.status === "active";
        if (filter === "archived") return field.status === "archived";
        return true;
    });

    // ‚ÜïÔ∏è –°–æ—Ä—Ç—É–≤–∞–Ω–Ω—è
    const sortedFields = [...filteredFields].sort((a, b) => {
        if (sortBy === "name") {
            return sortOrder === "asc" ? a.name.localeCompare(b.name) : b.name.localeCompare(a.name);
        }
        if (sortBy === "size") {
            return sortOrder === "asc" ? a.size - b.size : b.size - a.size;
        }
        if (sortBy === "created_at") {
            return sortOrder === "asc"
                ? new Date(a.createdAt) - new Date(b.createdAt)
                : new Date(b.createdAt) - new Date(a.createdAt);
        }
        return 0;
    });

    return (
        <div className="p-4">
            <h1 className="text-2xl font-semibold mb-4">–°–ø–∏—Å–æ–∫ –ø–æ–ª—ñ–≤</h1>
            <div className="space-y-4">
                {sortedFields.map(field => (
                    <div key={field.id} className="border p-4 rounded-lg">
                        <h2 className="text-xl">{field.name}</h2>
                        <p>–†–æ–∑–º—ñ—Ä: {field.size} –º¬≤</p>
                        <p>–°—Ç–∞—Ç—É—Å: {field.status}</p>
                        <p>–î–∞—Ç–∞ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è: {new Date(field.createdAt).toLocaleDateString()}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}
