import React, { useContext } from 'react'; // Додано useContext
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { LoadScript } from "@react-google-maps/api";
import { UserProvider, UserContext } from './contexts/UserContext'; // Імпортуємо UserContext

import Dashboard from './components/Dashboard';
import HomePage from './pages/HomePage';
import AnalyticsPage from './pages/AnalyticsPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import CreateFieldPage from './pages/CreateFieldPage';
import FieldPage from './pages/FieldPage';
import ProfilePage from './pages/ProfilePage';

import "./styles/App.css";
import './styles/index.css';

const Maps_API_KEY = "AIzaSyC6-KT4EaMNrVuln5DQ5Xn8e8n4kZwsKMU";






function App() {

      function ProtectedRoute({ children }) {
        const { user, loading } = useContext(UserContext);

        if (loading) return <div>Завантаження...</div>;

        return user ? children : <Navigate to="/login" replace />;
    }

    return (
        // LoadScript краще розмістити вище UserProvider, якщо API ключ не залежить від користувача
        <LoadScript googleMapsApiKey={Maps_API_KEY} libraries={['geometry']}>
            <Router>
                <UserProvider> {/* Тепер UserProvider огортає всі маршрути */}
                    <Routes>
                        {/* Публічні сторінки */}
                        <Route path="/login" element={<LoginPage />} />
                        <Route path="/register" element={<RegisterPage />} />

                        {/* Приватні сторінки огортаємо в ProtectedRoute */}
                        <Route
                            path="/"
                            element={

                                    <Dashboard />

                            }
                        >
                            {/* Вкладені маршрути автоматично стають захищеними */}
                            <Route index element={<Navigate to="home" replace />} /> {/* Додав replace */}
                            <Route path="home" element={<HomePage />} />
                            <Route path="create-field" element={<CreateFieldPage />} />
                            <Route path="field/:id/edit" element={<CreateFieldPage />} />
                            <Route path="field/:id" element={<FieldPage />} />
                            <Route path="analytics" element={<AnalyticsPage />} />
                            <Route path="profile" element={<ProfilePage />} />
                        </Route>

                        {/* Можна додати маршрут для неіснуючих сторінок */}
                        <Route path="*" element={<Navigate to={localStorage.getItem("userName") ? "/" : "/login"} replace />} />


                    </Routes>
                </UserProvider>
            </Router>
        </LoadScript>
    );
}

export default App;
