import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from "react-router-dom";

import api from "../utils/api";

const CreateField = () => {
    const navigate = useNavigate();
    const { id } = useParams(); // –æ—Ç—Ä–∏–º—É—î–º–æ id –∑ URL

    const [name, setName] = useState('');
    const [area, setArea] = useState('');
    const [geoZone, setGeoZone] = useState(null);
    const [manualCoords, setManualCoords] = useState('');

    useEffect(() => {
        if (id) {
            // —è–∫—â–æ —î ID ‚Äî —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
            api.get(`/fields/${id}`).then(res => {
                const field = res.data;
                setName(field.name);
                setArea(field.area);
                if (field.geo_zone?.coordinates?.[0]) {
                    setManualCoords(field.geo_zone.coordinates[0]
                        .map(pair => `[${pair[0]}, ${pair[1]}]`)
                        .join(',\n'));
                }
            }).catch(err => {
                console.error("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—ñ –ø–æ–ª—è", err);
                alert("–ù–µ –≤–¥–∞–ª–æ—Å—è –∑–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ –ø–æ–ª–µ");
            });
        }
    }, [id]);

    const handleCancel = () => {
        navigate(-1);
    };

    const parseManualCoords = () => {
        try {
            // –û—á–∏—Å—Ç–∏—Ç–∏ –≤—ñ–¥ –ø—Ä–æ–±—ñ–ª—ñ–≤, –ø–µ—Ä–µ–≤–æ–¥–∏–º–æ –≤ –º–∞—Å–∏–≤
            const cleaned = manualCoords
                .replace(/\s+/g, '') // –ø—Ä–∏–±–∏—Ä–∞—î –ø—Ä–æ–±—ñ–ª–∏/–ø–µ—Ä–µ–Ω–æ—Å–∏
                .replace(/\],\[/g, ']~[') // —Ä–æ–∑–¥—ñ–ª—è—î–º–æ –º—ñ–∂ –ø–∞—Ä–∞–º–∏
                .split('~')
                .map(pair => JSON.parse(pair)); // –ø–∞—Ä—Å–∏–º–æ –∫–æ–∂–Ω—É –ø–∞—Ä—É

            if (!Array.isArray(cleaned) || cleaned.length < 3) throw new Error("–ù–µ–¥–æ—Å—Ç–∞—Ç–Ω—å–æ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç");
            return cleaned;
        } catch (e) {
            alert("–ù–µ–≤—ñ—Ä–Ω–∏–π —Ñ–æ—Ä–º–∞—Ç –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç. –ü–µ—Ä–µ–≤—ñ—Ä—Ç–µ, —á–∏ –ø—Ä–∞–≤–∏–ª—å–Ω–æ –≤–∏ –≤–∫–∞–∑–∞–ª–∏ –≤—Å—ñ –¥—É–∂–∫–∏ —Ç–∞ –∫–æ–º–∏.");
            return null;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        let coordinates = geoZone;

        if (manualCoords.trim()) {
            const parsed = parseManualCoords();
            if (!parsed) return;
            coordinates = parsed;
        }

        if (!coordinates) {
            alert("–ë—É–¥—å –ª–∞—Å–∫–∞, –ø–æ–∑–Ω–∞—á—Ç–µ –ø–æ–ª–µ –Ω–∞ –∫–∞—Ä—Ç—ñ –∞–±–æ –≤–≤–µ–¥—ñ—Ç—å –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∏ –≤—Ä—É—á–Ω—É.");
            return;
        }

        const payload = {
            name,
            area: parseFloat(area),
            geo_zone: {
                type: "Polygon",
                coordinates: [coordinates]
            }
        };

        try {
            if (id) {
                await api.put(`/fields/${id}`, payload);
                alert("–ü–æ–ª–µ –æ–Ω–æ–≤–ª–µ–Ω–æ!");
            } else {
                await api.post('/fields', payload);
                alert("–ü–æ–ª–µ —É—Å–ø—ñ—à–Ω–æ —Å—Ç–≤–æ—Ä–µ–Ω–µ!");
            }
            navigate("/home");
        } catch (error) {
            console.error("–ü–æ–º–∏–ª–∫–∞:", error);
            alert(error?.response?.data?.error || "–©–æ—Å—å –ø—ñ—à–ª–æ –Ω–µ —Ç–∞–∫");
        }

    };

    return (
        <div className="w-full flex-1">
            <div className="p-6">
                <h5 className="font-bold text-black text-xl mb-4">–°—Ç–≤–æ—Ä–∏—Ç–∏ –Ω–æ–≤–µ –ø–æ–ª–µ</h5>
                <form onSubmit={handleSubmit}>
                    <label className="block mb-4">
                        –ù–∞–∑–≤–∞ –ø–æ–ª—è:
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                        />
                    </label>
                    <label className="block mb-4">
                        –ü–ª–æ—â–∞ –ø–æ–ª—è (–≤ –≥–∞):
                        <input
                            type="number"
                            value={area}
                            onChange={(e) => setArea(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg"
                            required
                            step="0.01"
                        />
                    </label>

                    {/* –ö–∞—Ä—Ç–∞ ‚Äî –ø—ñ–∑–Ω—ñ—à–µ */}
                    <div className="my-6">
                        <h6 className="font-semibold mb-2">–ü–æ–∑–Ω–∞—á—Ç–µ –ø–æ–ª–µ –Ω–∞ –∫–∞—Ä—Ç—ñ:</h6>
                        <div className="h-80 border rounded-lg flex items-center justify-center text-gray-500">
                            –ö–∞—Ä—Ç–∞ –Ω–µ–∑–∞–±–∞—Ä–æ–º —Ç—É—Ç üó∫Ô∏è
                        </div>
                    </div>

                    {/* –†—É—á–Ω–∏–π –≤–≤—ñ–¥ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç */}
                    <div className="my-6">
                        <h6 className="font-semibold mb-2">–ê–±–æ –≤–≤–µ–¥—ñ—Ç—å –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∏ —Ç—É—Ç:</h6>
                        <textarea
                            rows="6"
                            value={manualCoords}
                            onChange={(e) => setManualCoords(e.target.value)}
                            className="w-full border border-gray-300 p-2 rounded-lg font-mono text-sm"
                            placeholder={`[40.7484, -73.9857],\n[40.7488, -73.9855],\n[40.7486, -73.9842],\n[40.7482, -73.9844],\n[40.7484, -73.9857]`}
                        />
                        <p className="text-xs text-gray-500 mt-2">
                            –í–≤–µ–¥—ñ—Ç—å –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∏ —É —Ñ–æ—Ä–º–∞—Ç—ñ: <code>[—à–∏—Ä–æ—Ç–∞, –¥–æ–≤–≥–æ—Ç–∞]</code>, –∫–æ–∂–Ω–∞ –ø–∞—Ä–∞ –∑ –Ω–æ–≤–æ–≥–æ —Ä—è–¥–∫–∞. –ú–∞—î —É—Ç–≤–æ—Ä—é–≤–∞—Ç–∏ –∑–∞–º–∫–Ω–µ–Ω–∏–π –ø–æ–ª—ñ–≥–æ–Ω.
                        </p>
                    </div>

                    <button
                        type="submit"
                        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-full"
                    >

                            {id ? "–†–µ–¥–∞–≥—É–≤–∞—Ç–∏ –ø–æ–ª–µ" : "–°—Ç–≤–æ—Ä–∏—Ç–∏ –Ω–æ–≤–µ –ø–æ–ª–µ"}


                    </button>

                    <button
                        onClick={handleCancel}
                        className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full font-semibold mt-4"
                    >
                        –°–∫–∞—Å—É–≤–∞—Ç–∏
                    </button>
                </form>


            </div>
        </div>
    );
};

export default CreateField;
