import React, { createContext, useState, useEffect } from 'react';
import api from '../utils/api';

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [isAdmin, setIsAdmin] = useState(false);
    const [hasDevice, setHasDevice] = useState(false);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const res = await api.get('/users/me');
                if (res.data) {
                    setUser(res.data); // Оновлюємо користувача в контексті
                    setIsAdmin(res.data.is_admin || false); // Якщо користувач є адміністратором
                    // Перевіряємо, чи є пристрій у користувача
                    const devicesRes = await api.get('/user-iot-devices');
                    setHasDevice(devicesRes.data.length > 0); // Якщо є хоча б один пристрій
                }
            } catch (err) {
                console.log('Користувач не авторизований');
                setUser(null);
            } finally {
                setLoading(false);
            }
        };

        fetchUser();
    }, []);

    return (
        <UserContext.Provider value={{ user, isAdmin, hasDevice, loading }}>
            {children}
        </UserContext.Provider>
    );
};
