// src/components/AnalyticsContent.js
import React, { useEffect, useState } from 'react';
import api from '../utils/api';
// useFieldContext більше не потрібен тут

// Приймаємо пропс selectedFieldId
const AnalyticsContent = ({ selectedFieldId }) => {
    // Стан для діапазону часу (за замовчуванням тиждень)
    const [timeRange, setTimeRange] = useState('7d');
    // Стан для даних графіка
    const [chartData, setChartData] = useState(null);
    // Стан для індикатора завантаження
    const [isLoading, setIsLoading] = useState(false);
    // Стан для помилки
    const [error, setError] = useState(null);

    // Ефект для завантаження даних при зміні selectedFieldId або timeRange
    useEffect(() => {
        // useEffect спрацює при першому рендері, selectedFieldId може бути null
        if (!selectedFieldId) {
            // Якщо поле не обрано, скидаємо дані графіка та виходимо
            setChartData(null);
            setError(null);
            setIsLoading(false); // Ensure loading is false if no field is selected initially
            return;
        }

        setIsLoading(true);
        setError(null);
        // Формуємо URL запиту
        const apiUrl = `/field-measurements/chart/${selectedFieldId}/${timeRange}`;

        api.get(apiUrl)
            .then(response => {
                setChartData(response.data); // Зберігаємо отримані дані
                setIsLoading(false);
                // TODO: Call function to render the chart here using response.data
                // renderChart(response.data);
            })
            .catch(err => {
                console.error('Error fetching chart data:', err);
                setError('Не вдалося завантажити дані графіка.');
                setChartData(null); // Очищаємо дані при помилці
                setIsLoading(false);
            });

    }, [selectedFieldId, timeRange]); // Залежності ефекту

    // Функція для зміни діапазону часу
    const handleTimeRangeChange = (newTimeRange) => {
        setTimeRange(newTimeRange);
    };

    // TODO: Implement chart rendering function (e.g., using Chartist.js or another library)
    // const renderChart = (data) => {
    //   // Logic to draw the chart using the data and targeting the #chart1 div
    //   // Ensure you clear previous chart instances if necessary before drawing
    //   // e.g., if using Chartist: Chartist.create('#chart1', data, options);
    // };
    // useEffect(() => {
    //    if (chartData) {
    //        renderChart(chartData);
    //    } else {
    //        // Clear the chart div if chartData is null
    //        const chartDiv = document.getElementById('chart1');
    //        if (chartDiv) {
    //            chartDiv.innerHTML = ''; // Simple way to clear, might need library-specific clear logic
    //        }
    //    }
    // }, [chartData]); // Re-render chart when data changes


    return (
        <div id="main-content" className="w-full flex-1">
            <div className="flex flex-1 flex-wrap">
                <div className="w-full xl:w-2/3 p-6 xl:max-w-6xl"> {/* adjusted width slightly */}
                    <div className="max-w-full lg:max-w-3xl xl:max-w-5xl">
                        <div className="border-b p-3">
                            <h5 className="font-bold text-black">Графік вимірювань {selectedFieldId ? `для поля ID: ${selectedFieldId}` : ''}</h5> {/* Додали ID поля */}
                        </div>
                        <div className="p-5">
                            {/* Кнопки вибору масштабування */}
                            <div className="flex space-x-4 mb-4">
                                <button
                                    onClick={() => handleTimeRangeChange('7d')}
                                    className={`px-4 py-2 rounded ${timeRange === '7d' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Тиждень (7д)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('6m')}
                                    className={`px-4 py-2 rounded ${timeRange === '6m' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    6 місяців (6м)
                                </button>
                                <button
                                    onClick={() => handleTimeRangeChange('1y')}
                                    className={`px-4 py-2 rounded ${timeRange === '1y' ? 'bg-indigo-500 text-white' : 'bg-gray-300 text-gray-800'}`}
                                >
                                    Рік (1р)
                                </button>
                            </div>

                            {/* Область для відображення графіка */}
                            <div className="ct-chart ct-golden-section" id="chart1">
                                {!selectedFieldId && (
                                    <p className="text-center text-gray-500">Будь ласка, оберіть поле зі списку ліворуч, щоб побачити графік.</p>
                                )}
                                {selectedFieldId && isLoading && (
                                    <p className="text-center text-gray-500">Завантаження даних графіка...</p>
                                )}
                                {selectedFieldId && error && (
                                    <p className="text-center text-red-500">{error}</p>
                                )}
                                {/* Показуємо "дані не знайдені", тільки якщо поле обрано, не завантажується, без помилок, але даних немає */}
                                {selectedFieldId && !isLoading && !error && !chartData && (
                                    <p className="text-center text-gray-500">Дані для обраного поля за цей період не знайдені.</p>
                                )}
                                {/* TODO: Place your chart rendering logic here based on chartData */}
                                {/* Example: If using a React chart library that takes data as a prop */}
                                {/* {selectedFieldId && !isLoading && !error && chartData && (
                                    <MyChartComponent data={chartData} />
                                )} */}
                            </div>
                        </div>

                        {/* Розділ таблиці (залишаємо як було) */}
                        {/* TODO: можливо, цю таблицю теж зробити динамічною залежно від обраного поля? */}
                        <div className="p-3">
                            <div className="border-b p-3">
                                <h5 className="font-bold text-black">Table</h5>
                            </div>
                            <div className="p-5">
                                <table className="w-full p-5 text-gray-700">
                                    <thead>
                                    <tr>
                                        <th className="text-left text-blue-900">Name</th>
                                        <th className="text-left text-blue-900">Side</th>
                                        <th className="text-left text-blue-900">Role</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    <tr>
                                        <td>Obi Wan Kenobi</td>
                                        <td>Light</td>
                                        <td>Jedi</td>
                                    </tr>
                                    <tr>
                                        <td>Greedo</td>
                                        <td>South</td>
                                        <td>Scumbag</td>
                                    </tr>
                                    <tr>
                                        <td>Darth Vader</td>
                                        <td>Dark</td>
                                        <td>Sith</td>
                                    </tr>
                                    </tbody>
                                </table>
                                <p className="py-2"><a href="#">See More issues...</a></p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AnalyticsContent;
